{
  "schemaVersion": "2.2",
  "description": "Installs Datadog.",
  "parameters": {
    "workingDirectory": {
      "type": "String",
      "default": "/tmp",
      "description": "(Optional) The path to the working directory on your instance.",
      "maxChars": 4096
    }
  },
  "mainSteps": [
    {
      "action": "aws:runShellScript",
      "name": "install_datadog",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "inputs": {
        "runCommand": [
          "wget -O ddinstall.sh https://s3.amazonaws.com/dd-agent/scripts/install_script.sh",
          "export DD_API_KEY=$(aws --region us-west-2 ssm get-parameters --names /datadog/default/pti/api_key --with-decryption --query Parameters[].Value --output text)",
          "bash ./ddinstall.sh"
        ],
        "workingDirectory": "{{ workingDirectory }}",
        "timeoutSeconds": "3600"
      }
    }
  ]
}
