{
  "schemaVersion": "2.2",
  "description": "centrify-linux-domain-join",
  "parameters": {
    "resourceDomainName": {
      "description": "The FQDN of the Directory Service.",
      "type": "String"
    }
  },
  "mainSteps": [
    {
      "action": "aws:runShellScript",
      "name": "CentrifyLinuxDomainJoin",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "inputs": {
        "runCommand": [
          "sudo su - root",
          "cd /tmp",
          "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/centrify/scripts/centrify-linux-join.py .",
          "python3 ./centrify-linux-join.py {{ resourceDomainName }}",
          "rm ./centrify-linux-join.py"
        ]
      }
    }
  ]
}
