Content:
{
  "schemaVersion" : "0.3",
  "description" : "Given a running Instance with SSM Agent installed, upgrade to Windows Server 2012 R2, 2016 or 2019. The upgrade paths supported are 2008R2->2012R2, 2012R2->2016,  2012R2->2019 and 2016->2019. If your running instance in 2008R2, and you would like to  upgrade to either 2016 or 2019, the doc performs 2 steps, it first upgrades from 2008R2->2012R2  and upgrade 2012R2-2016 or 2012R2-2019. This document will create an AMI from the running instance and launch a new instance from the Image and will upgrade the new instance. Note that if the running instance does not have SSM installed, we will attempt to install SSM in the new instance before it is upgraded. The running instance remains untouched.",
  "parameters" : {
    "InstanceId" : {
      "type" : "String",
      "description" : "(Required) The Instance running Windows Server 2008 R2, 2012 R2 or 2016 with the SSM agent installed.",
      "allowedPattern" : "^i-[a-z0-9]{8,17}$"
    },
    "IamInstanceProfile" : {
      "type" : "String",
      "description" : "(Required) The IAM profile having access to run SSM on EC2 Instance. Please refer https://docs.aws.amazon.com/systems-manager/latest/userguide/sysman-configuring-access-role.html"
    },
    "SubnetId" : {
      "type" : "String",
      "description" : "(Required) Provide a subnet for the upgrade process to use. Please ensure the subnet has outbound connectivity to AWS services, S3, and Microsoft for download patches.",
      "allowedPattern" : "^subnet-[a-z0-9]{8,17}$"
    },
    "TargetWindowVersion" : {
      "type" : "String",
      "description" : "(Required) Select the target Windows version",
      "default" : "2012R2",
      "allowedValues" : [ "2012R2", "2016", "2019" ]
    },
    "BYOLWindowsMediaSnapshotId" : {
      "type" : "String",
      "description" : "(Optional) Required only if you are upgrading a BYOL instance. Please create a EBS snapshot of Windows Server 2012R2 Installation media using the steps detailed in the automation doc page.",
      "default" : "",
      "allowedPattern" : "^$|^snap-[a-z0-9]{8,17}$"
    },
    "AlternativeKeyPairName" : {
      "type" : "String",
      "description" : "(Optional) The name of an alternative key pair to use during the upgrade process. Useful in situations where the key pair used on the original instance is not available.",
      "default" : ""
    },
    "KeepPreUpgradeImageBackUp" : {
      "type" : "String",
      "description" : "(Optional) If set True SSM will keep the pre-upgrade image created from the instance and it's your responsibility to delete the Image. Default is False. ",
      "default" : "False",
      "allowedValues" : [ "True", "False" ]
    },
    "RebootInstanceBeforeTakingImage" : {
      "type" : "String",
      "description" : "(Optional) Default is False (no reboot). If set True, SSM will reboot the instance before creating an AMI for the upgrade. ",
      "default" : "False",
      "allowedValues" : [ "True", "False" ]
    }
  },
  "mainSteps" : [ {
    "name" : "putSSMInvocationMetric",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeWindows",
      "MetricName" : "Invocations",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "nextStep" : "describeOriginalInstanceDetails"
  }, {
    "name" : "describeOriginalInstanceDetails",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "InstanceIds" : [ "{{ InstanceId }}" ]
    },
    "outputs" : [ {
      "Name" : "InstanceType",
      "Selector" : "$.Reservations[0].Instances[0].InstanceType",
      "Type" : "String"
    }, {
      "Name" : "AvailabilityZone",
      "Selector" : "$.Reservations[0].Instances[0].Placement.AvailabilityZone",
      "Type" : "String"
    }, {
      "Name" : "RootDeviceName",
      "Selector" : "$.Reservations[0].Instances[0].RootDeviceName",
      "Type" : "String"
    }, {
      "Name" : "KeyName",
      "Selector" : "$.Reservations[0].Instances[0].KeyName",
      "Type" : "String"
    }, {
      "Name" : "SecurityGroupIds",
      "Selector" : "$.Reservations[0].Instances[0].SecurityGroups..GroupId",
      "Type" : "StringList"
    }, {
      "Name" : "BlockDeviceMappings",
      "Selector" : "$.Reservations[0].Instances[0].BlockDeviceMappings..DeviceName",
      "Type" : "StringList"
    } ],
    "nextStep" : "assertRootVolumeIsEbs"
  }, {
    "name" : "assertRootVolumeIsEbs",
    "action" : "aws:assertAwsResourceProperty",
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "InstanceIds" : [ "{{ InstanceId }}" ],
      "PropertySelector" : "$.Reservations[0].Instances[0].RootDeviceType",
      "DesiredValues" : [ "ebs" ]
    },
    "isCritical" : "true",
    "nextStep" : "assertSubnetHasAutoAssignIPV4"
  }, {
    "name" : "assertSubnetHasAutoAssignIPV4",
    "action" : "aws:assertAwsResourceProperty",
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeSubnets",
      "Filters" : [ {
        "Name" : "subnet-id",
        "Values" : [ "{{ SubnetId }}" ]
      } ],
      "PropertySelector" : "$.Subnets[0].MapPublicIpOnLaunch",
      "DesiredValues" : [ "True" ]
    },
    "isCritical" : "true",
    "nextStep" : "getOSVersion"
  }, {
    "name" : "getOSVersion",
    "action" : "aws:runCommand",
    "maxAttempts" : 3,
    "onFailure" : "step:putWindowsUpgradeFailureMetricNoSSM",
    "timeoutSeconds" : 600,
    "isCritical" : "true",
    "inputs" : {
      "DocumentName" : "AWS-RunPowerShellScript",
      "InstanceIds" : [ "{{ InstanceId }}" ],
      "Parameters" : {
        "executionTimeout" : "600",
        "commands" : [ "function Get-OSVersion {", "\t$osInfo = (Get-WmiObject -class Win32_OperatingSystem)", "\t# ProductType 3 = Server OS - Not a Domain Controller", "\tif ($osInfo.ProductType -ne 3) {", "\t\tWrite-Host \"This upgrade automation is not supported on Domain Controller or Work Station role.\"", "\t\texit 1", "\t}", "\t$OSVersion = $osInfo.Caption.ToUpper()", "\tif ($OSVersion.indexOf(\"2008 R2\") -ge 0) {", "\t\tWrite-Host -NoNewline \"2008R2\"", "\t}", "\telseif ($OSVersion.indexOf(\"2012 R2\") -ge 0) {", "\t\tWrite-Host -NoNewline \"2012R2\"", "\t}", "\telseif ($OSVersion.indexOf(\"2016\") -ge 0) {", "\t\tWrite-Host -NoNewline \"2016\"", "\t}", "\telse {", "\t\tWrite-Host \"This upgrade automation only supports Windows 2008R2/2012R2/2016.\"", "\t\texit 1", "\t}", "}", "", "Get-OSVersion" ]
      }
    },
    "nextStep" : "branchUpgradePath"
  }, {
    "name" : "putWindowsUpgradeFailureMetricNoSSM",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeWindows",
      "MetricName" : "Failure_Upgrades_No_SSM",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "isEnd" : "true"
  }, {
    "name" : "branchUpgradePath",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "branchOnRebootInstanceBeforeTakingImage",
        "Variable" : "{{ getOSVersion.Output }}",
        "StringEquals" : "2008R2"
      } ],
      "Default" : "runUpgradeFrom2012R2Or2016"
    },
    "isCritical" : "true"
  }, {
    "name" : "branchOnRebootInstanceBeforeTakingImage",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "createImageWithReboot",
        "Variable" : "{{ RebootInstanceBeforeTakingImage }}",
        "EqualsIgnoreCase" : "True"
      } ],
      "Default" : "createImageWithNoReboot"
    },
    "isCritical" : "true"
  }, {
    "name" : "createImageWithReboot",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "CreateImage",
      "InstanceId" : "{{ InstanceId }}",
      "Name" : "AWSEC2_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}",
      "NoReboot" : false
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "describeOriginalInstanceImage"
  }, {
    "name" : "createImageWithNoReboot",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "CreateImage",
      "InstanceId" : "{{ InstanceId }}",
      "Name" : "AWSEC2_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}",
      "NoReboot" : true
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "describeOriginalInstanceImage"
  }, {
    "name" : "describeOriginalInstanceImage",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeImages",
      "Filters" : [ {
        "Name" : "name",
        "Values" : [ "AWSEC2_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}" ]
      } ]
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.Images[0].ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "waitTillOriginalInstanceImageImageReady"
  }, {
    "name" : "waitTillOriginalInstanceImageImageReady",
    "action" : "aws:waitForAwsResourceProperty",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "maxAttempts" : 2,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeImages",
      "ImageIds" : [ "{{ describeOriginalInstanceImage.ImageId }}" ],
      "PropertySelector" : "$.Images[0].State",
      "DesiredValues" : [ "available" ]
    },
    "nextStep" : "branchUseAlternativeKeyPair"
  }, {
    "name" : "branchUseAlternativeKeyPair",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "serverUpgradeInstanceWithAlternativeKeyPair",
        "Not" : {
          "Variable" : "{{ AlternativeKeyPairName }}",
          "StringEquals" : ""
        }
      } ],
      "Default" : "serverUpgradeInstanceWithOriginalKeyPair"
    },
    "isCritical" : "true"
  }, {
    "name" : "serverUpgradeInstanceWithAlternativeKeyPair",
    "action" : "aws:runInstances",
    "timeoutSeconds" : 7200,
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "ImageId" : "{{ describeOriginalInstanceImage.ImageId }}",
      "InstanceType" : "{{ describeOriginalInstanceDetails.InstanceType }}",
      "MinInstanceCount" : 1,
      "MaxInstanceCount" : 1,
      "IamInstanceProfileName" : "{{ IamInstanceProfile }}",
      "SubnetId" : "{{ SubnetId }}",
      "KeyName" : "{{ AlternativeKeyPairName }}",
      "SecurityGroupIds" : [ "{{ describeOriginalInstanceDetails.SecurityGroupIds }}" ],
      "TagSpecifications" : [ {
        "ResourceType" : "instance",
        "Tags" : [ {
          "Key" : "Name",
          "Value" : "AWSEC2_UpgradeInstance_{{ automation:EXECUTION_ID }}"
        } ]
      } ],
      "UserData" : ""
    },
    "isCritical" : "true",
    "nextStep" : "getServerUpgradeInstance"
  }, {
    "name" : "serverUpgradeInstanceWithOriginalKeyPair",
    "action" : "aws:runInstances",
    "timeoutSeconds" : 7200,
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "ImageId" : "{{ describeOriginalInstanceImage.ImageId }}",
      "InstanceType" : "{{ describeOriginalInstanceDetails.InstanceType }}",
      "MinInstanceCount" : 1,
      "MaxInstanceCount" : 1,
      "IamInstanceProfileName" : "{{ IamInstanceProfile }}",
      "SubnetId" : "{{ SubnetId }}",
      "KeyName" : "{{ describeOriginalInstanceDetails.KeyName }}",
      "SecurityGroupIds" : [ "{{ describeOriginalInstanceDetails.SecurityGroupIds }}" ],
      "TagSpecifications" : [ {
        "ResourceType" : "instance",
        "Tags" : [ {
          "Key" : "Name",
          "Value" : "AWSEC2_UpgradeInstance_{{ automation:EXECUTION_ID }}"
        } ]
      } ],
      "UserData" : ""
    },
    "isCritical" : "true",
    "nextStep" : "getServerUpgradeInstance"
  }, {
    "name" : "getServerUpgradeInstance",
    "action" : "aws:executeAwsApi",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "Filters" : [ {
        "Name" : "tag:Name",
        "Values" : [ "AWSEC2_UpgradeInstance_{{ automation:EXECUTION_ID }}" ]
      } ]
    },
    "outputs" : [ {
      "Name" : "InstanceId",
      "Selector" : "$.Reservations[0].Instances[0].InstanceId",
      "Type" : "String"
    } ],
    "isCritical" : "true",
    "nextStep" : "waitForInstanceToReady"
  }, {
    "name" : "waitForInstanceToReady",
    "action" : "aws:waitForAwsResourceProperty",
    "onFailure" : "step:rebootToMakeItReady",
    "timeoutSeconds" : 600,
    "maxAttempts" : 2,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstanceStatus",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
      "PropertySelector" : "$.InstanceStatuses[0].InstanceStatus.Details[0].Status",
      "DesiredValues" : [ "passed" ]
    },
    "isCritical" : "false",
    "nextStep" : "checkForSSMAgent"
  }, {
    "name" : "rebootToMakeItReady",
    "action" : "aws:executeAwsApi",
    "onFailure" : "step:waitForInstanceToBeReadyAfterReboot",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "RebootInstances",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ]
    },
    "isCritical" : "false",
    "nextStep" : "waitForInstanceToBeReadyAfterReboot"
  }, {
    "name" : "waitForInstanceToBeReadyAfterReboot",
    "action" : "aws:waitForAwsResourceProperty",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "timeoutSeconds" : 600,
    "maxAttempts" : 2,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstanceStatus",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
      "PropertySelector" : "$.InstanceStatuses[0].InstanceStatus.Details[0].Status",
      "DesiredValues" : [ "passed" ]
    },
    "isCritical" : "true",
    "nextStep" : "checkForSSMAgent"
  }, {
    "name" : "checkForSSMAgent",
    "action" : "aws:waitForAwsResourceProperty",
    "timeoutSeconds" : 600,
    "maxAttempts" : 3,
    "onFailure" : "step:prepareServerInstanceForSSMInstall",
    "inputs" : {
      "Service" : "ssm",
      "Api" : "DescribeInstanceInformation",
      "InstanceInformationFilterList" : [ {
        "key" : "InstanceIds",
        "valueSet" : [ "{{ getServerUpgradeInstance.InstanceId }}" ]
      } ],
      "PropertySelector" : "$.InstanceInformationList[0].PingStatus",
      "DesiredValues" : [ "Online" ]
    },
    "isCritical" : "false",
    "nextStep" : "getUpgradeServerInstanceDetails"
  }, {
    "name" : "prepareServerInstanceForSSMInstall",
    "action" : "aws:executeAutomation",
    "maxAttempts" : 3,
    "timeoutSeconds" : 3600,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "DocumentName" : "AWSSupport-StartEC2RescueWorkflow",
      "RuntimeParameters" : {
        "InstanceId" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
        "OfflineScript" : [ "" ]
      }
    },
    "isCritical" : "true",
    "nextStep" : "startServerInstanceWithSSMInstall"
  }, {
    "name" : "startServerInstanceWithSSMInstall",
    "action" : "aws:changeInstanceState",
    "maxAttempts" : 3,
    "timeoutSeconds" : 600,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
      "CheckStateOnly" : false,
      "DesiredState" : "running"
    },
    "isCritical" : "true",
    "nextStep" : "waitSSMAgentOnlineOnServerUpgradeInstance"
  }, {
    "name" : "waitSSMAgentOnlineOnServerUpgradeInstance",
    "action" : "aws:waitForAwsResourceProperty",
    "timeoutSeconds" : 600,
    "maxAttempts" : 3,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ssm",
      "Api" : "DescribeInstanceInformation",
      "InstanceInformationFilterList" : [ {
        "key" : "InstanceIds",
        "valueSet" : [ "{{ getServerUpgradeInstance.InstanceId }}" ]
      } ],
      "PropertySelector" : "$.InstanceInformationList[0].PingStatus",
      "DesiredValues" : [ "Online" ]
    },
    "isCritical" : "true",
    "nextStep" : "getUpgradeServerInstanceDetails"
  }, {
    "name" : "getUpgradeServerInstanceDetails",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ]
    },
    "outputs" : [ {
      "Name" : "AvailabilityZone",
      "Selector" : "$.Reservations[0].Instances[0].Placement.AvailabilityZone",
      "Type" : "String"
    }, {
      "Name" : "SubnetId",
      "Selector" : "$.Reservations[0].Instances[0].NetworkInterfaces[0].SubnetId",
      "Type" : "String"
    } ],
    "isCritical" : "true",
    "nextStep" : "assertSupportedWindowsVersion"
  }, {
    "name" : "assertSupportedWindowsVersion",
    "action" : "aws:runCommand",
    "maxAttempts" : 3,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "timeoutSeconds" : 600,
    "isCritical" : "true",
    "nextStep" : "runScriptBeforeWindowServerUpgrade",
    "inputs" : {
      "DocumentName" : "AWS-RunPowerShellScript",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
      "Parameters" : {
        "executionTimeout" : "600",
        "commands" : [ "function Validate-InstanceLicensing([string]$WindowSnapshotId) {", "\t$IsError = $True", "\ttry {", "\t\t# compatible with PS2.0", "\t\t$webrequest = [System.Net.WebRequest]::Create(\"http://169.254.169.254/latest/dynamic/instance-identity/document\")", "\t\t$response = $webrequest.GetResponse()", "\t\t$stream = $response.GetResponseStream()", "\t\t$sr = new-object System.IO.StreamReader($stream)", "\t\t$content = $sr.ReadToEnd();", "\t\t[System.Reflection.Assembly]::LoadWithPartialName(\"System.Web.Extensions\") | out-null", "\t\t$ser = New-Object System.Web.Script.Serialization.JavaScriptSerializer", "\t\t$billingProduct = $ser.DeserializeObject($content).billingProducts", "\t\tif (($billingProduct -eq \"bp-6ba54002\") -or ($billingProduct -eq \"bp-68a54001\")) {", "\t\t\t$IsError = $False", "\t\t\tWrite-Host \"Instance has license included\"", "\t\t}", "\t\telseif (-not [string]::IsNullOrEmpty($WindowSnapshotId)) {", "\t\t\t$IsError = $False", "\t\t\tWrite-Host \"Instance will use $WindowSnapshotId for BYOL\"", "\t\t}", "\t\telse {", "\t\t\t$IsError = $True", "\t\t\tWrite-Host \"Your instance is BYOL, but you did not provide a BYOL Windows installation media snapshot id. Please re-run this automation with the necessary paramaters.\"", "\t\t}", "\t}", "\tcatch {", "\t\tWrite-Error $_.Exception.Message", "\t}", "\tfinally {", "\t\tif ($sr -ne $null) { $sr.Close(); }", "\t\tif ($response -ne $null) { $response.Close(); }", "\t\tif ($IsError) {", "\t\t\texit 1", "\t\t}", "\t\telse {", "\t\t\texit 0", "\t\t}", "\t}", "}", "", "function Check-OSUpgradeCompatibility {", "\t$osInfo = (Get-WmiObject -class Win32_OperatingSystem)", "\t# ProductType 3 = Server OS - Not a Domain Controller", "\tif ($osInfo.ProductType -ne 3) {", "\t\tWrite-Host \"This upgrade automation is not supported on Domain Controller or Work Station role. \"", "\t\texit 1", "\t}", "\t$OSVersion = $osInfo.Caption.ToUpper()", "\tif ($OSVersion.indexOf(\"2008 R2\") -ge 0) {", "\t\tWrite-Host \"OSVersion : $OSVersion \"", "\t}", "\telse {", "\t\tWrite-Host \"This upgrade automation only supports Windows 2008 R2.\"", "\t\texit 1", "\t}", "\t$Space = (Get-WMIObject Win32_Logicaldisk -filter \"deviceid='$($osInfo.systemdrive)'\").FreeSpace", "\t$AvailableGBSpace = [math]::floor($Space / 1GB)", "\tif ($AvailableGBSpace -le 20 ) {", "\t\tWrite-Host \"Not enough space to continue upgrade. Available space $AvailableGBSpace GB, minimum required is 20 GB\"", "\t\texit 1", "\t}", "}", "", "Check-OSUpgradeCompatibility", "$WindowSnapShotId = \"{{ BYOLWindowsMediaSnapshotId }}\"", "Validate-InstanceLicensing($WindowSnapShotId)" ]
      }
    }
  }, {
    "name" : "runScriptBeforeWindowServerUpgrade",
    "action" : "aws:runCommand",
    "maxAttempts" : 3,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "DocumentName" : "AWS-RunPowerShellScript",
      "InstanceIds" : [ "{{ getServerUpgradeInstance.InstanceId }}" ],
      "Parameters" : {
        "commands" : [ "function Enable-OfflineDisk {", "\t$offlinedisk = \"rescan`nlist disk\" | diskpart | Where-Object { $_ -match \"offline\" }", "\tif ($offlinedisk) {", "\t\tforeach ($offdisk in $offlinedisk) {", "\t\t\t$offdiskS = $offdisk.Substring(2, 6)", "\t\t\t$OnlineDisk = @\"", "select $offdiskS", "attributes disk clear readonly", "online disk", "attributes disk clear readonly", "\"@", "", "\t\t\t$noOut = $OnlineDisk | diskpart", "\t\t\tStart-Sleep 5", "\t\t}", "", "\t\tif (($offlinedisk = \"list disk\" | diskpart | Where-Object { $_ -match \"offline\" } )) {", "\t\t\t$offlinedisk", "\t\t}", "\t}", "}", "", "function GetSnapshot-ForCulture {", "\t$culture = Get-Culture", "\tif ($culture.Name -eq \"de-DE\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 German Installation Media\"", "\t}", "\telseif ($culture.Name -eq \"fr-FR\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 French Installation Media\"", "\t}", "\telseif ($culture.Name -eq \"ja-JP\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 Japanese Installation Media\"", "\t}", "\telseif ($culture.Name -eq \"cs-CZ\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 Czech Installation Media\"", "\t}", "\telseif ($culture.Name -eq \"pt-BR\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 Brazilian Installation Media\"", "\t}", "\telseif ($culture.Name -eq \"nl-NL\") {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 Dutch Installation Media\"", "\t}", "\telse {", "\t\tWrite-Host -NoNewline \"Windows 2012 R2 English Installation Media\"", "\t}", "}", "", "$TEMPSTORAGE = \"C:\\WindowsUpgrade\\{{ automation:EXECUTION_ID }}\"", "$TEMPSTOGAREFILE = \"$TEMPSTORAGE\\drive.txt\"", "Enable-OfflineDisk", "New-Item -ItemType directory -Path $TEMPSTORAGE | out-null", "([System.IO.DriveInfo]::getdrives() | select-object -ExpandProperty Name) -join ',' | Out-File \"$TEMPSTOGAREFILE\" | out-null", "GetSnapshot-ForCulture" ]
      }
    },
    "isCritical" : "true",
    "nextStep" : "getAmazonOwnedWindowsServerSnapShotId"
  }, {
    "name" : "getAmazonOwnedWindowsServerSnapShotId",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 3,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "timeoutSeconds" : 600,
    "inputs" : {
