{
  "schemaVersion": "0.3",
  "description": "Join instances to an AWS Directory Service domain.",
  "parameters": {
    "InstanceId": {
      "type": "StringList",
      "description": "(Required) EC2 Instance(s) to join"
    },
    "DirectoryName": {
      "type": "String",
      "description": "(Required) The name of the directory; for example, test.example.com"
    },
    "DomainUser": {
      "type": "String",
      "description": "(Required) The domain user"
    },
    "DomainPass": {
      "type": "String",
      "description": "(Required) The domain password"
    },
    "DCIPAddress": {
      "type": "String",
      "description": "(Required) The IP Address of the directory controller"
    }
  },
  "mainSteps": [
    {
      "name": "runJoinADShellScript",
      "action": "aws:runCommand",
      "inputs": {
        "DocumentName": "AWS-RunRemoteScript",
        "InstanceIds": [
          "{{InstanceId}}"
        ],
        "CloudWatchOutputConfig": {
          "CloudWatchOutputEnabled": "true",
          "CloudWatchLogGroupName": "DCPromoLogs"
        },
        "Parameters": {
          "sourceType": "S3",
          "sourceInfo": "{\"path\": \"https://s3-us-west-2.amazonaws.com/microsoft-servers-demo-8o3yew417f5r-us-west-2/adds_join_public.ps1\"}",
          "commandLine": "./adds_join_public.ps1 -DomainName {{DirectoryName}} -DomainUser {{DomainUser}} -DomainPass {{DomainPass}} -DCIPAddress {{DCIPAddress}}"
        }
      }
    }
  ]
}
