{
  "schemaVersion": "2.2",
  "description": "Command Document for Heat type mapping automation",
  "parameters": {
    "TenantID": {
      "type": "String",
      "description": "ID of tenant for which you want to run heat type mapping",
      "allowedPattern": "^[0-9]+[0-9\\s]*[0-9]+$"
    },
    "SourceFilePath": {
      "type": "String",
      "description": "Source file name with exact path.",
      "allowedPattern": "^[A-Za-z0-9\\/\\-_]+[A-Za-z0-9\\/\\s\\-_]*[A-Za-z\\.]+$"
    }
  },
  "mainSteps": [
    {
      "action": "aws:runShellScript",
      "name": "CluAutomation",
      "inputs": {
        "runCommand": [
          "sh /home/ec2-user/heat_type_automation/heat_type_automation.sh {{TenantID}} {{SourceFilePath}}"
        ]
      }
    }
  ]
}
