{
  "schemaVersion" : "2.2",
  "description" : "Wrapper document",
  "parameters" : {
    "s3ScriptPath" : {
      "type" : "String",
      "description" : "Path to wrapper script in S3."
    },
    "scriptName" : {
      "type" : "String",
      "description" : "Name of the script."
    },
    "scriptArgs" : {
      "type" : "String",
      "description" : "Arguments to the script."
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "runShellScript",
    "inputs" : {
      "runCommand" : [ "set -x;", "curl \"{{s3ScriptPath}}\" -o /tmp/{{scriptName}}", "chmod +x /tmp/{{scriptName}}", "/tmp/{{scriptName}} {{scriptArgs}}; returnCode=$?;", "rm -f /tmp/{{scriptName}}", "exit $returnCode" ]
    }
  } ]
}
