{
   "schemaVersion":"2.0",
   "description":"Blackhole a port on an instance",
   "parameters":{
      "port":{
         "type":"String",
         "description":"(Required) Specify a port to blackhole."
      },
      "duration":{
         "type":"String",
         "description":"(Required) Duration (in seconds) to blackhole the port."
      }
   },
   "mainSteps":[
      {
         "action":"aws:runShellScript",
         "name":"runShellScript",
         "inputs":{
            "runCommand":
            [
                "iptables -A INPUT -p tcp --destination-port {{ port }} -j DROP",
                "sleep {{ duration }}",
                "iptables -D INPUT -p tcp --destination-port {{ port }} -j DROP"
            ]
         }
      }
   ]
}
