{
  "description" : "Document to collect logs from IAM",
  "schemaVersion" : "2.2",
  "parameters" : {
    "environmentName" : {
      "default" : "",
      "description" : "Envrironment Name",
      "type" : "String"
    },
    "TenantName" : {
      "default" : "",
      "description" : "Tenant for which data need to be fetched",
      "type" : "String"
    },
    "BucketName" : {
      "default" : "",
      "description" : "Bucket for uploading and downloading data",
      "type" : "String"
    },
    "WorkDir" : {
      "default" : "/apps/dmpsec/AccessManager/OpenAM/openam/log",
      "description" : "Directory where operation will be getting perform",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "runShellScript",
    "inputs" : {
      "runCommand" : [ "sudo su -", "cd {{ WorkDir }}", "aws s3 cp s3://{{ BucketName }}/collect_iam_session_logs.sh ./", "chmod u+x collect_iam_session_logs.sh", "./collect_iam_session_logs.sh {{ TenantName }} {{ BucketName }}" ]
    }
  } ]
}
