Content:
{
  "schemaVersion": "2.2",
  "description": "cae-delete-ansible-user",
  "parameters": {
    "ssmuser": {
      "description": "User to create.",
      "type": "String"
    }
  },
  "mainSteps": [
    {
      "action": "aws:runPowerShellScript",
      "name": "DeleteWindowsAnsibleUser",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "inputs": {
        "runCommand": [
          "net user /delete {{ ssmuser }}",
          "$PROFILEPATH = [regex]::Escape('C:\\Users\\{{ ssmuser }}%')",
          "Get-WmiObject -Query \"SELECT * FROM Win32_UserProfile WHERE localpath LIKE '$PROFILEPATH'\" -ComputerName localhost |ForEach-Object { Remove-WmiObject -InputObject $_ }"
        ]
      }
    },
    {
      "action": "aws:runShellScript",
      "name": "DeleteLinuxAnsibleUser",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "inputs": {
        "runCommand": [
          "sudo su - root;",
          "pkill -U {{ ssmuser }};",
          "userdel -rf {{ ssmuser }};",
          "sed -i '/{{ ssmuser }}/d' /etc/sudoers;",
          "if [[ $(/usr/sbin/visudo -cf /etc/sudoers > /dev/null;echo $?) -eq 0 ]]; then echo 'The /etc/sudoers syntax is valid.'; else echo 'sudoers file is corrupt, fixing it...' && /usr/local/bin/sudoers_auto_heal.sh; fi;exit"
        ]
      }
    }
  ]
}
