{
  "schemaVersion": "2.2",
  "description": "Scans Filesystem for Log4J Prints to stdout",
  "mainSteps": [
    {
      "inputs": {
        "runCommand": [
          "$RunDate = (get-date).ToUniversalTime().ToString(\"yyyy-MM-ddTHH:mm+00:00\")\n $header = @{\"X-aws-ec2-metadata-token-ttl-seconds\" = \"21600\"}\n $token = Invoke-RestMethod -Method Put -Uri \"http://169.254.169.254/latest/api/token\" -Headers $header\n $header = @{\"X-aws-ec2-metadata-token\" = $token}\n $ec2info = Invoke-RestMethod -Headers $header -Method Get -Uri \"http://169.254.169.254/latest/meta-data/identity-credentials/ec2/info/\"\n $instanceid = Invoke-RestMethod -Headers $header -Method Get -Uri \"http://169.254.169.254/latest/meta-data/instance-id\"\n $region = Invoke-RestMethod -Headers $header -Method Get -Uri \"http://169.254.169.254/latest/meta-data/placement/availability-zone\"\n $hostname_ip = $env:computername + \"_\"+(Get-NetIPAddress -AddressFamily IPv4).IPAddress[0]\n $linecount = 0\n $Drives = Get-PSDrive -PSProvider 'FileSystem'\n foreach($Drive in $drives) { \n $log4jfiles = Get-Childitem â€“Path $Drive.Root -Include *log4j-core-*.jar -File -Recurse -ErrorAction SilentlyContinue\n   Foreach ($file in $log4jfiles)\n { \n Write-Host \"$($RunDate)`t$($ec2info.AccountId)`t$($region)`t$($instanceid)`t$($hostname_ip)`t$($file.FullName)\"\n $linecount += 1 } }\n if ($linecount -eq 0) {  Write-Host \"$($RunDate)`t$($ec2info.AccountId)`t$($region)`t$($instanceid)`t$($hostname_ip)`tnolog4jcore\" }"
        ]
      },
      "name": "log4jFileSystemQuery",
      "action": "aws:runPowerShellScript",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      }
    }
  ]
}
