{
  "description": "Automation Document Example JSON Template",
  "schemaVersion": "0.3",
  "assumeRole": "{{ AutomationAssumeRole }}",
  "parameters": {
    "InstanceId": {
      "type": "StringList",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": ["value1", "value2", "value3"]
    },
    "StringParameter": {
      "type": "String",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": "abc"
    }, 
    "MapParameter": {
      "type": "StringMap",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": {"key":"value"}
    },
    "StringListParameter": {
      "type": "StringList",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": ["value1", "value2", "value3"]
    },
    "BooleanParameter": {
      "type": "Boolean",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": false
    },
    "IntegerParameter": {
      "type": "Integer",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": 123
    },
    "MapListParameter": {
      "type": "MapList",
      "description": "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "default": [{"key1":"value1", "key2":"value2"}, {"key11":11, "key22":22}]
    }
  },
  "mainSteps": [
    {
      "name": "startInstances",
      "action": "aws:changeInstanceState",
      "inputs": {
        "InstanceIds": "{{ InstanceId }}",
        "DesiredState": "running"
      }
    }
  ]
}
