  "schemaVersion": "1.0",
  "description": "Document to run single non-interactive command on an instance",
  "sessionType": "NonInteractiveCommands",
  "parameters": {
    "command": {
      "type": "String",
      "description": "The command to run on the instance"
    }
  },
  "properties": {
    "windows": {
      "commands": "{{command}}",
      "runAsElevated": false
    },
    "linux": {
      "commands": "{{command}}",
      "runAsElevated": false
    },
    "macos": {
      "commands": "{{command}}",
      "runAsElevated": false
    }
  }
}
