{
    "description": "Install Flexera agent in beacon connected configuration.",
    "schemaVersion": "2.2",
    "parameters": {
        "FlexeraServerFQDN": {
            "type": "String",
            "default": "{{ssm:/CORP/Flexera/FlexeraServerFQDN}}"
        }
    },
    "mainSteps": [
        {
            "name": "runShellScript",
            "action": "aws:runShellScript",
            "precondition": {
                "StringEquals": [
                    "platformType",
                    "Linux"
                ]
            },
            "inputs": {
                "timeoutSeconds": "3600",
                "runCommand": [
                    "",
                    "ping -c 5 -w 5 {{FlexeraServerFQDN}} || exit 1",
                    "curl -s https://agent-scripts.ipc.corp.amway.net/flexera-scripts/AgentInstallation_LinuxRPM_14_2.zip -o /var/tmp/AgentInstallation_LinuxRPM_14_2.zip",
                    "unzip -u /var/tmp/AgentInstallation_LinuxRPM_14_2.zip -d /var/tmp/",
                    "sudo rpm --upgrade --verbose /var/tmp/managesoft-14.2.0-1.x86_64.rpm",
                    "sleep 30",
                    ""
                ]
            }
        },
        {
            "name": "runPowerShellScript",
            "action": "aws:runPowerShellScript",
            "precondition": {
                "StringEquals": [
                    "platformType",
                    "Windows"
                ]
            },
            "inputs": {
                "timeoutSeconds": "3600",
                "runCommand": [
                    "",
                    "$ErrorActionPreference = 'Stop'",
                    "Test-Connection -ComputerName '{{FlexeraServerFQDN}}'",
                    "$ZIPFILE='AgentInstallation_Windows_14_2.zip'",
                    "$TEMPDIR=[System.IO.Path]::GetTempPath()",
                    "$OUTFILE=Join-Path -Path $TEMPDIR -ChildPath $ZIPFILE",
                    "[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12",
                    "Invoke-WebRequest -Uri https://agent-scripts.ipc.corp.amway.net/flexera-scripts/$ZIPFILE -OutFile $OUTFILE",
                    "Expand-Archive -LiteralPath $OUTFILE -DestinationPath $TEMPDIR -Force",
                    "Set-Location -Path $TEMPDIR",
                    "msiexec.exe /i 'FlexNet Inventory Agent.msi' /qn",
                    "Start-Sleep -Seconds 60",
                    ""
                ]
            }
        }
    ]
}
