{
    "description": "Run Flexera standalone inventory agent.",
    "schemaVersion": "2.2",
    "mainSteps": [
        {
            "name": "runShellScript",
            "action": "aws:runShellScript",
            "precondition": {
                "StringEquals": [
                    "platformType",
                    "Linux"
                ]
            },
            "inputs": {
                "timeoutSeconds": "3600",
                "runCommand": [
                    "",
                    "ZIPFILE='StandaloneInventoryAgent_LinuxRPM_14_1.zip'",
                    "rm -f /var/flexera/agent/*",
                    "mkdir -p /var/flexera/agent/",
                    "[ ! -f /var/flexera/$ZIPFILE ] && curl -s https://agent-scripts.ipc.corp.amway.net/flexera-scripts/${ZIPFILE} -o /var/flexera/$ZIPFILE",
                    "unzip /var/flexera/$ZIPFILE -d /var/flexera/agent/",
                    "chmod u+x /var/flexera/agent/ndtrack.sh",
                    "/var/flexera/agent/ndtrack.sh -t machine -o Upload=False -o IncludeDirectory='/opt','/usr','/var' -o MachineInventoryDirectory=/var/flexera/agent -o LogFile=/var/flexera/agent/tracker.log",
                    "sleep 60",
                    "aws s3 cp /var/flexera/agent/*.ndi s3://flexera-inventory-files/ --acl bucket-owner-full-control",
                    "sleep 30",
                    "rm -f /var/flexera/agent/*",
                    ""
                ]
            }
        },
        {
            "name": "runPowerShellScript",
            "action": "aws:runPowerShellScript",
            "precondition": {
                "StringEquals": [
                    "platformType",
                    "Windows"
                ]
            },
            "inputs": {
                "timeoutSeconds": "3600",
                "runCommand": [
                    "",
                    "$ZIPFILE='StandaloneInventoryAgent_Windows_14_1.zip'",
                    "New-Item -ItemType Directory -Path 'C:\\flexera\\agent' -Force",
                    "[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12",
                    "Invoke-WebRequest -Uri https://agent-scripts.ipc.corp.amway.net/flexera-scripts/$ZIPFILE -OutFile C:\\flexera\\$ZIPFILE",
                    "Expand-Archive -LiteralPath C:\\flexera\\$ZIPFILE -DestinationPath C:\\flexera\\agent -Force",
                    "C:\\flexera\\agent\\ndtrack.exe -t machine -o Upload=False -o IncludeDirectory='C:\\Program Files (x86)','D:\\Program Files (x86)','C:\\Program Files','D:\\Program Files' -o MachineZeroTouchDirectory=C:\\flexera\\agent -o LogFile=C:\\flexera\\agent\\tracker.log",
                    "$SleepCounter = 0; While (Get-Process -Name 'ndtrack.exe' -ErrorAction 'SilentlyContinue') {Write-Host 'Sleeping'; $SleepCounter += 3; Start-Sleep -Seconds 3; If ($SleepCounter -gt 60) {break}}",
                    "Start-Sleep -Seconds 60",
                    "Write-S3Object -BucketName flexera-inventory-files -Folder C:\\flexera\\agent -KeyPrefix \\ -SearchPattern *.ndi -CannedACL bucket-owner-full-control",
                    "Start-Sleep -Seconds 30",
                    ""
                ]
            }
        }
    ]
}
