{
  "schemaVersion" : "2.2",
  "description" : "centrify-domain-join",
  "parameters" : {
    "resourceDomainName" : {
      "description" : "The FQDN of the Directory Service.",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "CentrifyLinuxDomainJoin",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "cd /tmp", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/centrify/scripts/prod/centrify-linux-join.py .", "python3 ./centrify-linux-join.py {{ resourceDomainName }}", "rm ./centrify-linux-join.py" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "CentrifyWindowsDomainJoin",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "cd c:/windows/temp", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/centrify/scripts/prod/ad-windows-join.ps1 .", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/centrify/scripts/prod/centrify-windows-join.ps1 .", ". c:/windows/temp/ad-windows-join.ps1 {{ resourceDomainName }}", ". c:/windows/temp/centrify-windows-join.ps1 {{ resourceDomainName }}" ]
    }
  } ]
}
