    "Conditions" : {
      "EnablePrometheus" : {
        "Fn::Or" : [ {
          "Fn::Equals" : [ {
            "Ref" : "PrometheusIntegration"
          }, "Enabled" ]
        }, {
          "Fn::Equals" : [ {
            "Ref" : "MonitoringStack"
          }, "Prometheus + Grafana" ]
        } ]
      },
      "EnableGrafana" : {
        "Fn::Or" : [ {
          "Fn::Equals" : [ {
            "Ref" : "GrafanaIntegration"
          }, "Enabled" ]
        }, {
          "Fn::Equals" : [ {
            "Ref" : "MonitoringStack"
          }, "Prometheus + Grafana" ]
        } ]
      },
      "DetectSharedStacks" : {
        "Fn::And" : [ {
          "Fn::Equals" : [ {
            "Ref" : "PerAccountSharedResources"
          }, "AutoDetect" ]
        }, {
          "Fn::Equals" : [ {
            "Ref" : "PerRegionSharedResources"
          }, "AutoDetect" ]
        } ]
      },
      "CreateAdvancedConfigWithDefaults" : {
        "Fn::Equals" : [ {
          "Ref" : "ConfigSetName"
        }, "" ]
      },
      "CreatePerAccountSharedResources" : {
        "Fn::Equals" : [ {
          "Ref" : "PerAccountSharedResources"
        }, "Yes" ]
      },
      "CreatePerRegionSharedResources" : {
        "Fn::Equals" : [ {
          "Ref" : "PerRegionSharedResources"
        }, "Yes" ]
      },
      "3AZDeployment" : {
        "Fn::Equals" : [ {
          "Ref" : "NumberOfAZs"
        }, "3" ]
      },
      "2AZDeployment" : {
        "Fn::Or" : [ {
          "Fn::Equals" : [ {
            "Ref" : "NumberOfAZs"
          }, "2" ]
        }, {
          "Fn::Equals" : [ {
            "Ref" : "NumberOfAZs"
          }, "3" ]
        } ]
      },
      "UsingDefaultBucket" : {
        "Fn::Equals" : [ {
          "Ref" : "QSS3BucketName"
        }, "aws-quickstart" ]
      },
      "WindowsNodes" : {
        "Fn::Equals" : [ {
          "Ref" : "NodeGroupOS"
        }, "Windows" ]
      },
      "VaultEnabled" : {
        "Fn::Equals" : [ {
          "Ref" : "VaultIntegration"
        }, "Enabled" ]
      }
    },
    "Mappings" : {
      "Config" : {
        "Prefix" : {
          "Value" : "eks-quickstart"
        }
      }
    },
    "Resources" : {
      "AdvancedConfigDefaultsStack" : {
        "Type" : "AWS::CloudFormation::Stack",
        "Condition" : "CreateAdvancedConfigWithDefaults",
        "Metadata" : {
          "cfn-lint" : {
            "config" : {
              "ignore_checks" : [ "E9902", "W9901" ]
            }
          }
        },
        "Properties" : {
          "TemplateURL" : {
            "Fn::Sub" : [ "https://${S3Bucket}.s3.${S3Region}.${AWS::URLSuffix}/${QSS3KeyPrefix}templates/amazon-eks-advanced-configuration.template.yaml", {
              "S3Region" : {
                "Fn::If" : [ "UsingDefaultBucket", {
                  "Ref" : "AWS::Region"
                }, {
                  "Ref" : "QSS3BucketRegion"
                } ]
              },
              "S3Bucket" : {
                "Fn::If" : [ "UsingDefaultBucket", {
                  "Fn::Sub" : "${QSS3BucketName}-${AWS::Region}"
                }, {
                  "Ref" : "QSS3BucketName"
                } ]
              }
            } ]
          },
          "Parameters" : {
            "ConfigSetName" : {
              "Ref" : "AWS::StackName"
            },
            "NodeVolumeSize" : {
              "Fn::If" : [ "WindowsNodes", 50, {
                "Ref" : "AWS::NoValue"
              } ]
            },
            "KubernetesVersion" : {
              "Fn::If" : [ "VaultEnabled", "1.17", {
                "Fn::If" : [ "WindowsNodes", "1.20", {
                  "Ref" : "AWS::NoValue"
                } ]
              } ]
            },
            "ConsulUIAccessCIDR" : {
              "Ref" : "RemoteAccessCIDR"
            },
            "VaultUIAccessCIDR" : {
              "Ref" : "RemoteAccessCIDR"
            }
          }
        }
      },
      "AutoDetectSharedResources" : {
        "Type" : "AWS::CloudFormation::Stack",
        "Condition" : "DetectSharedStacks",
        "Properties" : {
          "TemplateURL" : {
            "Fn::Sub" : [ "https://${S3Bucket}.s3.${S3Region}.${AWS::URLSuffix}/${QSS3KeyPrefix}templates/amazon-eks-prerequisites.template.yaml", {
              "S3Region" : {
                "Fn::If" : [ "UsingDefaultBucket", {
                  "Ref" : "AWS::Region"
                }, {
                  "Ref" : "QSS3BucketRegion"
                } ]
              },
              "S3Bucket" : {
                "Fn::If" : [ "UsingDefaultBucket", {
                  "Fn::Sub" : "${QSS3BucketName}-${AWS::Region}"
                }, {
                  "Ref" : "QSS3BucketName"
                } ]
              }
            } ]
          },
