{
  "schemaVersion" : "2.2",
  "description" : "Run PowerShell commands on Windows instances and shell commands on Linux instances",
  "mainSteps" : [ {
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "action" : "aws:runShellScript",
    "name" : "runShellScriptLinux",
    "inputs" : {
      "runCommand" : [ "set -ex", "curl -fs --header 'PRIVATE-TOKEN:glpat-yGynE8woSZFRhY4hdrHD' 'https://gitlab.com/api/v4/projects/26402772/repository/files/build%2Fscripts%2F_common%2Fsecurity_stack%2Esh/raw?ref=HEAD' | sudo bash" ]
    }
  }, {
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "action" : "aws:runPowerShellScript",
    "name" : "runPowerShellScript",
    "inputs" : {
      "runCommand" : [ "Write-Output \"Windows not supported\"" ]
    }
  }, {
    "precondition" : {
      "StringEquals" : [ "platformType", "MacOS" ]
    },
    "action" : "aws:runShellScript",
    "name" : "runShellScriptMac",
    "inputs" : {
      "runCommand" : [ "echo \"MacOS not supported\"" ]
    }
  } ]
}
