{
  "schemaVersion" : "2.2",
  "description" : "Uninstall IPP Service",
  "parameters" : {
    "usvctl" : {
      "type" : "String",
      "default" : "usvctl"
    },
    "packgeName" : {
      "type" : "String"
    },
    "directoryLinux" : {
      "type" : "String",
      "default" : "/usr/local/ipp"
    },
    "directoryWindows" : {
      "type" : "String",
      "default" : "C:\\Program Files\\ipp"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runPowerShellScript",
    "name" : "windows_1_uninstall_service",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "If (Test-Path \"{{ directoryWindows }}\\{{ packgeName }}\") {", "    & {{ directoryWindows }}\\{{ packgeName }}\\{{ usvctl }}.exe stop --name {{ packgeName }}", "    & {{ directoryWindows }}\\{{ packgeName }}\\{{ usvctl }}.exe uninstall --name {{ packgeName }}", "    & Remove-Item -LiteralPath \"{{ directoryWindows }}\\{{ packgeName }}\" -Force -Recurse", "}" ]
    }
  }, {
    "action" : "aws:runShellScript",
    "name" : "linux_1_uninstall_package",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "#!/usr/bin/env bash", "set +e", "if [ -d {{ directoryLinux }}/{{ packgeName }} ]", "then", "    {{ directoryLinux }}/{{ packgeName }}/{{ usvctl }} stop --name {{ packgeName }}", "    {{ directoryLinux }}/{{ packgeName }}/{{ usvctl }} uninstall --name {{ packgeName }}", "    rm -rf {{ directoryLinux }}/{{ packgeName }}", "fi" ]
    }
  } ]
}
