{
  "schemaVersion": "2.2",
  "description": "Execute Automox install scripts stored in a remote location.",
  "parameters": {
    "URL": {
      "type": "String",
      "default": "{{ssm:/CORP/Automox/URLJP}}"
    },
    "Key": {
      "type": "String",
      "default": "{{ssm:/CORP/Automox/KeyJP}}"
    }
  },
  "mainSteps": [
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "action": "aws:runPowerShellScript",
      "name": "runPowerShellScript",
      "inputs": {
        "runCommand": [
          "",
          "$directory = Convert-Path .",
          "$env:PATH += \";$directory\"",
          "powershell",
          "mkdir C:/ssmtemp/",
          "[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12",
          "iwr -uri \"https://agent-scripts.ipc.corp.amway.net/automox-scripts/Automox_Installer.msi\" -OutFile C:\\ssmtemp\\Automox_Installer.msi",
          "C:\\ssmtemp\\Automox_Installer.msi accesskey='{{Key}}' /quiet",
          "Start-Sleep -s 30",
          "Remove-Item -LiteralPath \"C:/ssmtemp/\" -Force -Recurse",
          "if ($?) {",
          "    exit $LASTEXITCODE",
          "} else {",
          "    exit 255",
          "}",
          ""
        ],
        "timeoutSeconds": "3600"
      }
    },
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "action": "aws:runShellScript",
      "name": "runShellScript",
      "inputs": {
        "runCommand": [
          "",
          "sudo yum-config-manager --enable epel",
          "directory=$(pwd)",
          "curl -sS {{URL}} | sudo bash",
          "sudo service amagent start",
          ""
        ],
        "timeoutSeconds": "3600"
      }
    }
  ]
}
