{
  "schemaVersion" : "2.2",
  "description" : "delinea-domain-join",
  "parameters" : {
    "resourceDomainName" : {
      "description" : "The FQDN of the Directory Service .",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "DelineaLinuxDomainJoin",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "cd /tmp", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/delinea/scripts/dev/delinea-linux-join.py .", "python3 ./delinea-linux-join.py {{ resourceDomainName }}", "rm ./delinea-linux-join.py" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "DelineaWindowsDomainJoin",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "cd c:/windows/temp", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/delinea/scripts/dev/ad-windows-join.ps1 .", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/delinea/scripts/dev/delinea-windows-join.ps1 .", ". c:/windows/temp/ad-windows-join.ps1 {{ resourceDomainName }}", ". c:/windows/temp/delinea-windows-join.ps1 {{ resourceDomainName }}" ]
    }
  } ]
}
