Content:
{
  "description" : "",
  "schemaVersion" : "0.3",
  "parameters" : {
    "InstanceId" : {
      "type" : "String",
      "description" : "(Required) Instance Id of the instance to install the package."
    },
    "action" : {
      "type" : "String",
      "description" : "(Required) Specify whether or not to install or uninstall the package.",
      "allowedValues" : [ "Install", "Uninstall" ]
    },
    "installationType" : {
      "type" : "String",
      "description" : "(Optional) Specify the type of installation. Uninstall and reinstall: The application is taken offline until the reinstallation process completes. In-place update: The application is available while new or updated files are added to the installation.",
      "allowedValues" : [ "Uninstall and reinstall", "In-place update" ],
      "default" : "Uninstall and reinstall"
    },
    "name" : {
      "type" : "String",
      "description" : "(Required) The package to install/uninstall."
    },
    "version" : {
      "type" : "String",
      "description" : "(Optional) The version of the package to install or uninstall. If you donâ€™t specify a version, the system installs the latest published version by default. The system will only attempt to uninstall the version that is currently installed. If no version of the package is installed, the system returns an error.",
      "default" : ""
    }
  },
  "mainSteps" : [ {
    "name" : "InstallPackage",
    "action" : "aws:runCommand",
    "inputs" : {
      "DocumentName" : "AWS-ConfigureAWSPackage",
      "InstanceIds" : [ "{{InstanceId}}" ],
      "Parameters" : {
        "action" : "{{action}}",
        "installationType" : "{{installationType}}",
        "name" : "{{name}}",
        "version" : "{{version}}"
      }
    },
    "description" : "Install or uninstall a Distributor package. You can install the latest version, default version, or a version of the package you specify. Packages provided by AWS such as AmazonCloudWatchAgent, AwsEnaNetworkDriver, and AWSPVDriver are also supported."
  } ]
}
