{
  "description" : "*This is a test.*  ",
  "schemaVersion" : "0.3",
  "assumeRole" : "{{AutomationAssumeRole}}",
  "parameters" : {
    "InstanceId" : {
      "type" : "String",
      "description" : "Run powershell command against"
    },
    "AutomationAssumeRole" : {
      "type" : "String",
      "description" : "'(Optional) The Amazon Resource Name (ARN) of the IAM role that allows Automation to perform the actions on your behalf. If no role is specified, Systems Manager Automation uses your IAM permissions to operate this runbook.'",
      "default" : ""
    }
  },
  "mainSteps" : [ {
    "name" : "runPowerShell",
    "action" : "aws:runCommand",
    "inputs" : {
      "DocumentName" : "AWS-RunPowerShellScript",
      "InstanceIds" : [ "{{InstanceId}}" ],
      "Parameters" : {
        "commands" : [ "$computer = hostname", "$Cert = New-SelfSignedCertificate -DnsName \"$computer\" -certstorelocation cert:/localmachine/my;", "$thumbprint = $Cert.Thumbprint", "$port=5986", "WinRM e winrm/config/listener", "Start-Sleep -Seconds 10", "New-Item WSMan:\\localhost\\Listener -Address * -Transport HTTPS -HostName $computer -CertificateThumbPrint $thumbprint -Force" ]
      }
    }
  } ]
}
