Content:
{
  "schemaVersion" : "2.2",
  "description" : "Install And Run IPP Chaos Client.",
  "parameters" : {
    "linuxInstallDirectory" : {
      "type" : "String",
      "default" : "/tmp"
    },
    "windowsInstallDirectory" : {
      "type" : "String",
      "default" : "C:\\tmp"
    },
    "s3UrlBase" : {
      "type" : "String",
      "default" : "https://s3-us-west-2.amazonaws.com"
    },
    "s3Bucket" : {
      "type" : "String"
    },
    "s3UrlVersion" : {
      "type" : "String",
      "default" : "latest"
    },
    "VPCEndpoint" : {
      "type" : "String"
    },
    "HSAPIToken" : {
      "type" : "String"
    },
    "Region" : {
      "type" : "String"
    },
    "ChaosCommand" : {
      "type" : "String"
    },
    "ChaosDuration" : {
      "type" : "String"
    },
    "ChaosJobId" : {
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "1_prepareEnv_linux",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "mkdir -p {{ linuxInstallDirectory }}" ]
    }
  }, {
    "action" : "aws:downloadContent",
    "name" : "2_downloadChaosclient_linux",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "sourceType" : "S3",
      "sourceInfo" : "{\"path\": \"{{ s3UrlBase }}/{{ s3Bucket }}/{{ s3UrlVersion }}/exec/amd64/linux/chaosclient\"}",
      "destinationPath" : "{{ linuxInstallDirectory }}"
    }
  }, {
    "action" : "aws:runShellScript",
    "name" : "3_executeChaosclient_linux",
    "timeoutSeconds" : 30,
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "chmod +x {{ linuxInstallDirectory }}/chaosclient", "export HOLOSENSE_VPC_ENDPOINT={{ VPCEndpoint }}", "export HOLOSENSE_HSAPI_TOKEN={{ HSAPIToken }}", "export HOLOSENSE_REGION={{ Region }}", "{{ linuxInstallDirectory }}/chaosclient -d {{ ChaosDuration }} -j {{ ChaosJobId }} {{ ChaosCommand }}" ]
    }
  }, {
    "action" : "aws:runShellScript",
    "name" : "4_cleanUp_linux",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "rm {{ linuxInstallDirectory }}/chaosclient" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "1_prepareEnv_windows",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "New-Item -Path {{ windowsInstallDirectory }} -Type Directory" ]
    }
  }, {
    "action" : "aws:downloadContent",
    "name" : "2_downloadChaosclient_windows",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "sourceType" : "S3",
      "sourceInfo" : "{\"path\": \"{{ s3UrlBase }}/{{ s3Bucket }}/{{ s3UrlVersion }}/exec/amd64/windows/chaosclient.exe\"}",
      "destinationPath" : "{{ windowsInstallDirectory }}"
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "3_executeChaosclient_windows",
    "timeoutSeconds" : 30,
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "$env:HOLOSENSE_VPC_ENDPOINT=\"{{ VPCEndpoint }}\"", "$env:HOLOSENSE_HSAPI_TOKEN=\"{{ HSAPIToken }}\"", "$env:HOLOSENSE_REGION=\"{{ Region }}\"", "{{ windowsInstallDirectory }}\\chaosclient.exe -d {{ ChaosDuration }} -j {{ ChaosJobId }} {{ ChaosCommand }}", "if (!$?) { exit 1 }" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "4_cleanUp_windows",
    "timeoutSeconds" : 30,
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "Remove-Item -Force -Path {{ windowsInstallDirectory }}\\chaosclient.exe" ]
    }
  } ]
}
