{
  "schemaVersion" : "2.2",
  "description" : "eis-branding-config",
  "parameters" : {
    "customer" : {
      "description" : "The customer short name (lowercase)",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "EisBrandingConfig",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "mkdir -p /root/eisbranding", "brandingType=\"custom\"", "aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/$brandingType-branding.py /root/eisbranding/" ]
    }
  } ]
}
