{
  "schemaVersion" : "2.2",
  "description" : "palantir",
  "parameters" : {
    "customer" : {
      "description" : "The customer short name (lowercase)",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "PalantirLinux",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "mkdir -p /root/palantir", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/palantir/scripts/prod/ec2_audit.py /root/palantir/", "python3 /root/palantir/ec2_audit.py {{ customer }}", "aws s3 sync /root/palantir/ s3://ellucian-{{ customer }}-artifacts/palantir", "rm -rf /root/palantir" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "PalantirWindows",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "mkdir c:/Ellucian/palantir", "aws s3 cp --only-show-errors s3://ellucian-global-artifacts/AWS-MODDATA-GLOBAL/palantir/scripts/prod/ec2_audit.ps1 c:/Ellucian/palantir", ". c:/palantir/ec2_audit.ps1 {{ customer }}", "aws s3 sync c:/Ellucian/palantir s3://ellucian-{{ customer }}-artifacts/palantir", "rm -rf c:/Ellucian/palantir" ]
    }
  } ]
}
