Content:
{
  "schemaVersion" : "2.2",
  "description" : "Grant user access",
  "parameters" : {
    "pubkey" : {
      "type" : "String",
      "description" : "(Required) public key to add"
    },
    "authorizedKeysFile" : {
      "type" : "String",
      "description" : "(Optional) Path to authorized_keys file.",
      "default" : "/home/ec2-user/.ssh/authorized_keys"
    },
    "AccountID" : {
      "type" : "String",
      "description" : "(Required) Account ID"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "runShellScript",
    "inputs" : {
      "runCommand" : [ "#!/bin/sh", "set -e", "sudo yum install jq -y", "pubkey=\"{{pubkey}}\"", "authorized_key_file={{authorizedKeysFile}}", "AccountID={{AccountID}}", "", "echo Switching role to $iamrole", "mkdir -p ~/.aws", "aws sts assume-role --role-arn  arn:aws:iam::$AccountID:role/SSHPubKeyAccess --role-session-name sshkeyaccess > ~/.aws/sshkeyaccess.txt", "export AWS_ACCESS_KEY_ID=$(cat ~/.aws/sshkeyaccess.txt | jq -r .Credentials.AccessKeyId)", "export AWS_SECRET_ACCESS_KEY=$(cat ~/.aws/sshkeyaccess.txt | jq -r .Credentials.SecretAccessKey)", "export AWS_SESSION_TOKEN=$(cat ~/.aws/sshkeyaccess.txt | jq -r .Credentials.SessionToken)", "", "", "[[ -e $authorized_key_file ]] || touch $authorized_key_file && chmod 744 $authorized_key_file", "", "  echo \"Installing key $pubkey into $authorized_key_file\"", "  echo \"$pubkey \" >> $authorized_key_file", "", "cat $authorized_key_file", "echo 'Keys installed!'", "", "unset AWS_ACCESS_KEY_ID", "unset AWS_SECRET_ACCESS_KEY", "unset AWS_SESSION_TOKEN", "rm ~/.aws/sshkeyaccess.txt" ]
    }
  } ]
}
