{
  "schemaVersion" : "2.2",
  "description" : "eis-branding-config",
  "parameters" : {
    "brandingType" : {
      "description" : "The branding type the customer has",
      "type" : "String"
    },
    "eisShortname" : {
      "description" : "The customer shortname",
      "type" : "String"
    },
    "customerRegion" : {
      "description" : "The customer region",
      "type" : "String"
    },
    "eisDeploymentVersion" : {
      "description" : "The eis version",
      "type" : "String"
    },
    "eisDeploymentEnvironment" : {
      "description" : "The eis environment",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "EisBrandingConfig",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "mkdir -p /root/eisbranding", "aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/{{ brandingType }}-branding.py /root/eisbranding/", "python3 /root/eisbranding/{{ brandingType }}-branding.py {{ eisShortname }} {{ customerRegion }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }}", "rm -rf /root/eisbranding" ]
    }
  } ]
}
