{
  "schemaVersion" : "2.2",
  "description" : "eis-branding-config",
  "parameters" : {
    "brandingType" : {
      "description" : "The branding type the customer has",
      "type" : "String"
    },
    "eisShortname" : {
      "description" : "The customer shortname",
      "type" : "String"
    },
    "customerRegion" : {
      "description" : "The customer region",
      "type" : "String"
    },
    "eisDeploymentVersion" : {
      "description" : "The eis version",
      "type" : "String"
    },
    "eisDeploymentEnvironment" : {
      "description" : "The eis environment",
      "type" : "String"
    },
    "brandingColorsDark" : {
      "description" : "The rgb dark colors for branding",
      "type" : "String"
    },
    "brandingColorsLight" : {
      "description" : "The rgb light colors for branding",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "EisBrandingConfig",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "tempPath='/root/eisbranding'", "webappsPath='/u01/app/ellucian/EllucianEthosIdentity/wso2is-5.10.0/repository/deployment/server/webapps/authenticationendpoint'", "mkdir -p $tempPath", "aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/{{ brandingType }}-branding.py $tempPath/", "if [ {{ brandingType }} == 'custom' ]; then python3 $tempPath/{{ brandingType }}-branding.py {{ eisShortname }} {{ customerRegion }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }}; fi", "if [ {{ brandingType }} == 'basic' ]; then aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/auth_index.css.j2 $webappsPath/ellucian/index.css; aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/auth_basicauth.jsp $webappsPath/basicauth.jsp; aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/auth_login.jsp $webappsPath/login.jsp; aws s3 cp --only-show-errors s3://ellucian-capps-artifacts/eis-branding/nonprod/scripts/auth_logout.jsp $webappsPath/logout.jsp; python3 $tempPath/{{ brandingType }}-branding.py {{ eisShortname }} {{ customerRegion }} {{ brandingColorsDark }} {{ brandingColorsLight }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }}; fi", "rm -rf $tempPath" ]
    }
  } ]
}
