{
  "schemaVersion": "1.2",
  "description": "Backup logs to S3",
  "parameters": {
    "ASGNAME": {
      "type": "String",
      "description": "Auto Scaling group name"
    },
    "LIFECYCLEHOOKNAME": {
      "type": "String",
      "description": "LIFECYCLEHOOK name"
    },
    "BACKUPDIRECTORY": {
      "type": "String",
      "description": "BACKUPDIRECTORY localtion in server"
    },
    "S3BUCKET": {
      "type": "String",
      "description": "S3BUCKET backup logs"
    },
    "SNSTARGET": {
      "type": "String",
      "description": "SNSTARGET"
    }
  },
  "runtimeConfig": {
    "aws:runShellScript": {
      "properties": [
        {
          "id": "0.aws:runShellScript",
          "runCommand": [
            "sudo /etc/init.d/ssp-server stop;/opt/log_utility/log_utility /opt/log_utility/log_util_config_org.yaml shutdown"
          ]
        }
      ]
    }
  }
}
