{
  "schemaVersion" : "2.2",
  "description" : "Installs PowerShell Core Edition on Ubuntu Linux, using the package from Microsoft. The AWS-RunPowerShellScript document isn't compatible with the Snap package for PowerShell, as it resides under a different filesystem path (/snap/bin/pwsh) instead of /usr/bin/pwsh. Developed by Trevor Sullivan at CBT Nuggets.",
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "InstallPowerShellCore",
    "inputs" : {
      "runCommand" : [ "apt-get update", "apt-get install -y wget apt-transport-https software-properties-common", "wget -q https://packages.microsoft.com/config/ubuntu/20.04/packages-microsoft-prod.deb", "dpkg -i packages-microsoft-prod.deb", "apt-get update", "add-apt-repository universe", "apt-get install -y powershell" ]
    }
  } ]
}
