Content:
{
  "schemaVersion": "2.2",
  "description": "cae-create-ansible-user",
  "parameters": {
    "ssmuser": {
      "description": "User to create.",
      "type": "String"
    },
    "ssmpassword": {
      "description": "Encrypted Password to use.",
      "type": "String"
    }
  },
  "mainSteps": [
    {
      "action": "aws:runPowerShellScript",
      "name": "CreateWindowsAnsibleUser",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      },
      "inputs": {
        "runCommand": [
          "net user /delete {{ ssmuser }}",
          "$password=$(ConvertTo-SecureString '{{ ssmpassword }}' -AsPlainText -Force)",
          "New-LocalUser -Name '{{ ssmuser }}' -Description 'CAE Ansible Account.' -Password $password",
          "Add-LocalGroupMember -Group 'Administrators' -Member '{{ ssmuser }}'"
        ]
      }
    },
    {
      "action": "aws:runShellScript",
      "name": "CreateLinuxAnsibleUser",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "inputs": {
        "runCommand": [
          "sudo su - root",
          "pkill -U {{ ssmuser }}",
          "userdel -rf {{ ssmuser }}",
          "useradd -g users -p $(echo '{{ ssmpassword }}' | openssl passwd -1 -stdin) {{ ssmuser }}",
          "if [[ $(/usr/sbin/visudo -cf /etc/sudoers > /dev/null;echo $?) -eq 0 ]]; then echo 'sudoers was found to be in a healthy state'; else echo 'sudoers was found in a broken state, fix it...' && /usr/local/bin/sudoers_auto_heal.sh; fi",
          "echo 'writing to sudoers file...' && echo '{{ ssmuser }} ALL=(ALL) NOPASSWD:ALL' >> /etc/sudoers.d/ell-cae-ansible-user && chmod 440 /etc/sudoers.d/ell-cae-ansible-user",
          "if [[ $(/usr/sbin/visudo -cf /etc/sudoers > /dev/null;echo $?) -eq 0 ]]; then echo 'sudoers was found to be in a healthy state'; else echo 'our change broke sudoers so we must restore it...' && /usr/local/bin/sudoers_auto_heal.sh; fi;exit"]
      }
    }
  ]
}
