{
  "schemaVersion" : "2.2",
  "description" : "Run a script on Linux instances.",
  "parameters" : {
    "commands" : {
      "default" : "echo Hello World",
      "description" : "The commands to run or the path to an existing script on the instance.",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "inputs" : {
      "timeoutSeconds" : "60",
      "runCommand" : [ "{{ commands }}" ]
    },
    "name" : "runCommands",
    "action" : "aws:runShellScript"
  } ]
}
