Content:
{
  "schemaVersion" : "2.2",
  "description" : "Cloning a file/repo from bitbucket",
  "parameters" : {
    "SourcePath" : {
      "type" : "String",
      "description" : "Source Path"
    },
    "BranchName" : {
      "type" : "String",
      "description" : "branch name"
    },
    "DestinationPath" : {
      "type" : "String",
      "description" : "Destination Path"
    },
    "BitbucketRepo" : {
      "type" : "String",
      "description" : "Bitbucket repo path"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runPowerShellScript",
    "name" : "CopyFiles",
    "inputs" : {
      "runCommand" : [ "try{", "Add-Type -AssemblyName System.Web", "New-Item -ItemType \"directory\" -Path {{DestinationPath}}", "cd {{DestinationPath}}", "git init", "git config core.sparsecheckout true", "New-Item -Path .\\.git\\info\\ -Name \"sparse-checkout\" -ItemType \"file\" -Value {{SourcePath}}", "$userName=(Get-SSMParameter -Name /bitbucket/svc/username).Value", "$password=[System.Web.HttpUtility]::UrlEncode((Get-SSMParameter -Name /bitbucket/svc/password -WithDecryption $True).Value)", "$repo={{BitbucketRepo}}", "$repoDetails=$repo.replace(\"https://\",\"https://${userName}:${password}@\")", "git pull $repoDetails {{BranchName}}", "} catch {", "Write-Host \"An error occurred:\"", "Write-Host $_.ScriptStackTrace", "Write-Host $_.Exception", "Write-Host $_.ErrorDetails }" ]
    }
  } ]
}
