{
  "schemaVersion" : "2.2",
  "description" : "eis-branding-config",
  "parameters" : {
    "brandingType" : {
      "description" : "The branding type the customer has",
      "type" : "String"
    },
    "eisShortname" : {
      "description" : "The customer shortname",
      "type" : "String"
    },
    "customerRegion" : {
      "description" : "The customer region",
      "type" : "String"
    },
    "eisDeploymentVersion" : {
      "description" : "The eis version",
      "type" : "String"
    },
    "eisDeploymentEnvironment" : {
      "description" : "The eis environment",
      "type" : "String"
    },
    "brandingColorsDark" : {
      "description" : "The rgb dark colors for branding",
      "type" : "String"
    },
    "brandingColorsLight" : {
      "description" : "The rgb light colors for branding",
      "type" : "String"
    },
    "logoHeight" : {
      "description" : "The logo height",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "EisBrandingConfig",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root", "mkdir -p /root/eisbranding", "if [ {{ eisShortname }} == 'capps' ]; then aws s3 cp --only-show-errors s3://ellucian-global-artifacts/eis-artifacts/branding-files/sandbox/{{ eisDeploymentVersion }}/{{ brandingType }}-branding.py /root/eisbranding/; else aws s3 cp --only-show-errors s3://ellucian-global-artifacts/eis-artifacts/branding-files/production/{{ eisDeploymentVersion }}/{{ brandingType }}-branding.py /root/eisbranding/; fi", "if [ {{ brandingType }} == 'custom' ]; then python /root/eisbranding/custom-branding.py {{ eisShortname }} {{ customerRegion }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }}; fi", "if [ {{ brandingType }} == 'basic' ]; then python /root/eisbranding/basic-branding.py {{ eisShortname }} {{ customerRegion }} {{ brandingColorsDark }} {{ brandingColorsLight }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }} {{ logoHeight }}; fi", "if [ {{ brandingType }} == 'default' ]; then python /root/eisbranding/default-branding.py {{ eisShortname }} {{ customerRegion }} {{ eisDeploymentVersion }} {{ eisDeploymentEnvironment }}; fi", "rm -rf /root/eisbranding" ]
    }
  } ]
}
