{
  "schemaVersion": "2.2",
  "description": "Command Document Example JSON Template",
  "parameters": {
    "Action": {
      "type": "String",
      "description": "Operation you want to perform using automation",
      "allowedValues": [
        "create",
        "rerun_from_start",
        "rerun_from_training",
        "data_migrate",
        "delete"
      ]
    },
    "TenantName": {
      "type": "String",
      "description": "Name of tenant for which you want to run CLU training",
      "allowedPattern": "^[A-Za-z0-9]+[A-Za-z0-9\\s]*[A-Za-z0-9]+$"
    },
    "TenantID": {
      "type": "String",
      "description": "ID of tenant for which you want to run CLU training",
      "allowedPattern": "^[0-9]+[0-9\\s]*[0-9]+$"
    }
  },
  "mainSteps": [
    {
      "action": "aws:runShellScript",
      "name": "CluAutomation",
      "inputs": {
        "timeoutSeconds": "172800",
        "runCommand": [
          "a=0",
          "b=1",
          "cmd=\"/home/ec2-user/scripts/clu_automation_training.sh \"",
          "cmd=\"$cmd {{Action}}\"",
          "for i in {{TenantID}}",
          "do",
          "a=$((a+1))",
          "b=1",
          "for j in {{TenantName}}",
          "do",
          "if [ $a = $b ]; then",
          "cmd=\"$cmd $j  $i\"",
          "fi",
          "b=$((b+1))",
          "done",
          "done",
          "echo \"Final command: $cmd \"",
          "sh $cmd"
        ]
      }
    }
  ]
}
