{
  "description": "Rapid7 agent uninstall",
  "schemaVersion": "2.2",
  "parameters": {
    "AccountId": {
      "default": "12345678",
      "description": "Aws AccountId",
      "type": "String"
    },
    "Platform": {
      "allowedValues": [
        "NA",
        "UK",
        "CSID"
      ],
      "description": "Select region you are in",
      "type": "String"
    }
  },
  "mainSteps": [
    {
      "inputs": {
        "timeoutSeconds": 7200,
        "runCommand": [
          "#!/bin/bash",
          "sudo su -",
          "systemctl stop ir_agent",
          "wget --directory-prefix=/tmp/rapid https://javaqualysagnetinstallation.s3-us-west-2.amazonaws.com/agents-linux.tar.gz",
          "tar -xzf /tmp/rapid/agents-linux.tar.gz --directory /tmp/rapid  && cd /tmp/rapid/agents-linux/",
          "chmod +x agent_installer.sh",
          "./agent_installer.sh uninstall || true",
          "cd ~ && rm -rf /tmp/rapid"
        ]
      },
      "name": "PatchLinux",
      "action": "aws:runShellScript",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      }
    },
    {
      "inputs": {
        "timeoutSeconds": 7200,
        "runCommand": [
          "New-Item -Path 'C:\\' -Name \"rapid\" -ItemType \"directory\";",
          "write-host $Platform",
          "cd C:\\rapid;",
          "(New-Object System.Net.WebClient).DownloadFile('https://javaqualysagnetinstallation.s3-us-west-2.amazonaws.com/agents-win.zip', 'C:\\rapid\\agents-win.zip');",
          "expand-archive -path 'C:\\rapid\\agents-win.zip' -destinationpath 'C:\\rapid';",
          "sc stop ir_agent",
          "msiexec /i agentInstaller-x86_64.msi uinstall",
          "cd ~;",
          "Start-Sleep -s 60;",
          "Remove-Item -LiteralPath 'C:\\rapid' -Force -Recurse"
        ]
      },
      "name": "PatchWindows",
      "action": "aws:runPowerShellScript",
      "precondition": {
        "StringEquals": [
          "platformType",
          "Windows"
        ]
      }
    }
  ]
}
