Content:
{
  "schemaVersion" : "0.3",
  "description" : "SSM Automation for upgrading SQL Server 2008 (or later) to SQL Server 2014/2016/2017/2019.  The running instance remains untouched, while the automation upgrades a cloned instance. If the instance is running Windows Server 2008 R2, the automation will upgrade the operating system to Windows Server 2012 R2. If you are upgrading the SQL Server to 2019  and your Windows is 2008/2012, it will first upgrade your Windows Server to 2016 and upgrade  SQL Server afterwards.",
  "parameters" : {
    "InstanceId" : {
      "type" : "String",
      "description" : "(Required) The instance running Windows Server 2008 R2 or above, SQL Server 2008 or above, with the SSM agent installed."
    },
    "IamInstanceProfile" : {
      "type" : "String",
      "description" : "(Required) The IAM instance profile managed policy attached. Please refer to https://docs.aws.amazon.com/systems-manager/latest/userguide/sysman-configuring-access-role.html for more information."
    },
    "SubnetId" : {
      "type" : "String",
      "description" : "(Required) Provide a subnet for the upgrade process. Verify that the subnet has outbound connectivity to AWS services, Amazon S3, and Microsoft (to download patches).",
      "allowedPattern" : "^subnet-[a-z0-9]{8,17}$"
    },
    "SQLServerSnapshotId" : {
      "type" : "String",
      "description" : "(Conditional) The snapshot id for target SQL Server installation media. This parameter  is not required for SQL Server license-included instances (instances launched using an  AWS provided Amazon Machine Image for Windows Server with Microsoft SQL Server), only  required if you are running BYOL instance.",
      "default" : "",
      "allowedPattern" : "(^$|^snap-[0-9a-f]+$)"
    },
    "AlternativeKeyPairName" : {
      "type" : "String",
      "description" : "(Optional) The name of an alternative key pair to use during the upgrade process. Useful in situations where the key pair used on the original instance is not available.",
      "default" : ""
    },
    "KeepPreUpgradeImageBackUp" : {
      "type" : "String",
      "description" : "(Optional) If set to True, the Automation doesn't delete the AMI created from the instance before the upgrade. By default, the AMI is deleted.",
      "default" : "False",
      "allowedValues" : [ "True", "False" ]
    },
    "RebootInstanceBeforeTakingImage" : {
      "type" : "String",
      "description" : "(Optional) If set to True, the Automation reboots the instance before creating a pre-upgrade AMI. By default, the Automation doesn't reboot before upgrade.",
      "default" : "False",
      "allowedValues" : [ "True", "False" ]
    },
    "TargetSQLVersion" : {
      "type" : "String",
      "description" : "(Optional) Select the target SQL Server version. Default is SQL Server 2016.",
      "default" : "2016",
      "allowedValues" : [ "2014", "2016", "2017", "2019" ]
    }
  },
  "mainSteps" : [ {
    "name" : "putSSMInvocationMetric",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeSQLServer",
      "MetricName" : "Invocations",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "nextStep" : "assertSSMAgentOnline"
  }, {
    "name" : "assertSSMAgentOnline",
    "action" : "aws:assertAwsResourceProperty",
    "maxAttempts" : 3,
    "timeoutSeconds" : 300,
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ssm",
      "Api" : "DescribeInstanceInformation",
      "InstanceInformationFilterList" : [ {
        "key" : "InstanceIds",
        "valueSet" : [ "{{ InstanceId }}" ]
      } ],
      "PropertySelector" : "$.InstanceInformationList[0].PingStatus",
      "DesiredValues" : [ "Online" ]
    },
    "isCritical" : "true",
    "nextStep" : "assertReadyToUpgradeSQLServer"
  }, {
    "name" : "assertReadyToUpgradeSQLServer",
    "action" : "aws:runCommand",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "timeoutSeconds" : 300,
    "inputs" : {
      "DocumentName" : "AWS-RunPowerShellScript",
      "InstanceIds" : [ "{{ InstanceId }}" ],
      "Parameters" : {
        "executionTimeout" : "300",
        "commands" : [ "function Check-SqlUpgradeEligibility {", "$osInfo = (Get-WmiObject -class Win32_OperatingSystem)", "# https://docs.microsoft.com/en-us/windows/desktop/CIMWin32Prov/win32-operatingsystem ProductType 3 is Server\" ,", "if ($osInfo.ProductType -ne 3) {", "Write-Host \"Server Upgrade not allowed if Server is of type Work Station OR Domain Controller.\"", "exit 1", "}", "", "$OSVersion = $osInfo.Caption.ToUpper()", "$instances = (get-itemproperty \"HKLM:\\SOFTWARE\\Microsoft\\Microsoft SQL Server\").InstalledInstances", "if ($instances.count -eq 0) {", "Write-Host \"Couldn't find any installed instance of SQL Server.\"", "exit 1", "}", "if ($instances.count -gt 1) {", "Write-Host \"Found multiple instances of SQL Server, presently not supported\"", "exit 1", "}", "$instance = (Get-ItemProperty \"HKLM:\\SOFTWARE\\Microsoft\\Microsoft SQL Server\\Instance Names\\SQL\").$instances", "$instanceDetails = (Get-ItemProperty \"HKLM:\\SOFTWARE\\Microsoft\\Microsoft SQL Server\\$instance\\Setup\")", "$edition = $instanceDetails.Edition", "", "$targetSQLVersion = {{ TargetSQLVersion }}", "", "Write-Host \"SQL Server Edition is $edition\"", "", "if ($instanceDetails.Cluster) {", "Write-Host \"SQL Server is clustered, presently not supported\"", "exit 1", "}", "$v = $instanceDetails.Version.subString(0, $instanceDetails.Version.indexOf(\".\"))", "if ($v -ge 10) {", "if ($v -eq 10 ) {", "if ($instanceDetails.SP -lt 3) {", "Write-host \"SQL Server Version 2008 , apply patch for SQL Server 2008\"", "}", "ElseIf ($instanceDetails.SP -eq 3) {", "Write-host \"Supported SQL Version 2008 R2 SP3\"", "}", "}", "else {", "if (($v -eq 13) -and ($targetSQLVersion -eq '2014'))", "{ ", "Write-Host \"Current SQL Version 2016, cannot downgrade to 2014.\"", "exit 1", "}", "elseif (($v -eq 14) -and (($targetSQLVersion -eq '2016') -or ($targetSQLVersion -eq '2014')))", "{ ", "Write-Host \"Current SQL Version 2017, cannot downgrade to $targetSQLVersion.\"", "exit 1", "}", "elseif (($v -eq 15) -and (($targetSQLVersion -eq '2017') -or ($targetSQLVersion -eq '2016') -or ($targetSQLVersion -eq '2014')))", "{ ", "Write-Host \"Current SQL Version 2019, cannot downgrade to $targetSQLVersion.\"", "  exit 1", "}", "else", "{ ", "Write-host \"Supported SQL Version\"", "}", "}", "}", "else {", "Write-host \" SQL Server Version $instanceDetails.Version is presently not supported\"", "exit 1", "}", "[System.Reflection.Assembly]::LoadWithPartialName(\"Microsoft.SqlServer.SMO\") | out-null", "$SQLServer = New-Object (\"Microsoft.SqlServer.Management.Smo.Server\")", "if([string]::IsNullOrEmpty($SQLServer.VersionString)) {$SQLServer=New-Object (\"Microsoft.SqlServer.Management.Smo.Server\") \"$env:COMPUTERNAME\\$instances\"} ", "Foreach ($Database in $SQLServer.Databases) {", "if ($Database.IsMirroringEnabled) {", "$Name = $Database.Name", "Write-Host \"Mirroring is enabled for DB $Name \"", "Write-Host \" Presently we are not supporting Mirroring instances \"", "exit 1", "}", "}", "", "$targetVersion = {{ TargetSQLVersion }}", "if ((($OSVersion.indexOf(\"2019\")  -ge 0) -or ($OSVersion.indexOf(\"2016\")  -ge 0)) -and (($OSVersion.indexOf(\"STANDARD\") -ge 0) -or ($OSVersion.indexOf(\"DATACENTER\") -ge 0))) {", "Write-Host \"OSVersion : $OSVersion \"", "}", "elseif (($OSVersion.indexOf(\"2012\") -ge 0 ) -and (($OSVersion.indexOf(\"STANDARD\") -ge 0) -or ($OSVersion.indexOf(\"DATACENTER\") -ge 0))) {", "if($targetVersion -eq \"2019\")", "{", "Write-Host \"WINDOWSUPGRADE 2016 Will be done first\"", "}", "else", "{", "Write-Host \"OSVersion : $OSVersion \"", "}", "}", "elseif ($OSVersion.indexOf(\"2008\") -ge 0 -and (($OSVersion.indexOf(\"STANDARD\") -ge 0) -or ($OSVersion.indexOf(\"DATACENTER\") -ge 0))) {", "Write-Host \"WINDOWSUPGRADE 2012 Will be done first\"", "}", "else {", "Write-Host \"This document support upgrades of 2012 R2/RTM Standard, 2012 R2/RTM Datacenter, 2016 Standard and 2016 Datacenter\"", "exit 1", "}", "}", "", "", "function Validate-InstanceLicensing([string]$SnapshotId) {", "$IsError = $True", "try {", "# signature verification for IMDS", "Add-Type -AssemblyName System.Security", "$region = \"us-west-2\"", "[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12", "$certificate_string = (Invoke-RestMethod -Uri \"https://s3.us-west-2.amazonaws.com/aws.portingassistant.dotnet.datastore/certificates/imds_certificates.json\").certificates.$region", "$certificate_bytes_arr = [system.Text.Encoding]::UTF8.GetBytes($certificate_string)", "$token = (Invoke-RestMethod -Method Put -Uri http://169.254.169.254/latest/api/token -Headers @{\"X-aws-ec2-metadata-token-ttl-seconds\"=\"60\"})", "$Signature = [Convert]::FromBase64String((Invoke-WebRequest -UseBasicParsing -Headers @{'X-aws-ec2-metadata-token' = [string]$token} http://169.254.169.254/latest/dynamic/instance-identity/rsa2048).Content)", "$Document = [Text.Encoding]::UTF8.GetBytes((Invoke-WebRequest -UseBasicParsing -Headers @{'X-aws-ec2-metadata-token' = [string]$token} http://169.254.169.254/latest/dynamic/instance-identity/document).Content)", "$X509Certificate2 = New-Object -TypeName Security.Cryptography.X509Certificates.X509Certificate2 -ArgumentList @(,$certificate_bytes_arr)", "$Store = New-Object -TypeName Security.Cryptography.X509Certificates.X509Certificate2Collection -ArgumentList $X509Certificate2", "$SignatureDocument = New-Object -TypeName Security.Cryptography.Pkcs.SignedCms", "$SignatureDocument.Decode($Signature)", "$verify_result = $SignatureDocument.CheckSignature($Store, $true)", "if ($verify_result) {", "throw $verify_result", "}", "if(![Linq.Enumerable]::SequenceEqual($SignatureDocument.ContentInfo.Content, $Document)) {", "throw \"Instance Identity Document Cannot be Validated.\"", "}", "", "# compatible with PS2.0", "$token = (Invoke-RestMethod -Method Put -Uri http://169.254.169.254/latest/api/token -Headers @{\"X-aws-ec2-metadata-token-ttl-seconds\"=\"60\"})", "$webrequest = [System.Net.WebRequest]::Create(\"http://169.254.169.254/latest/dynamic/instance-identity/document\")", "$webrequest.Headers.Add(\"X-aws-ec2-metadata-token\",$token)", "$response = $webrequest.GetResponse()", "$stream = $response.GetResponseStream()", "$sr = new-object System.IO.StreamReader($stream)", "$content = $sr.ReadToEnd();", "[System.Reflection.Assembly]::LoadWithPartialName(\"System.Web.Extensions\") | out-null", "$ser = New-Object System.Web.Script.Serialization.JavaScriptSerializer", "$billingProduct = $ser.DeserializeObject($content).billingProducts", "if (($billingProduct -eq \"bp-6aa54003\") -or ($billingProduct -eq \"bp-62a5400b\") -or ($billingProduct -eq \"bp-65a5400c\")) {", "$IsError = $False", "Write-Host \"Instance has license included\"", "}", "elseif (-not [string]::IsNullOrEmpty($SnapshotId)) {", "$IsError = $False", "Write-Host \"Instance will use $SnapshotId for BYOL\"", "}", "else {", "$IsError = $True", "Write-Host \"Your instance is BYOL, but you did not provide a BYOL Windows installation media snapshot id. Please re-run this automation with the necessary paramaters.\"", "}", "}", "catch {", "Write-Error $_.Exception.Message", "}", "finally {", "if ($sr -ne $null) { $sr.Close(); }", "if ($response -ne $null) { $response.Close(); }", "if ($IsError) {", "exit 1", "}", "else {", "exit 0", "}", "}", "}", "", "try {", "Check-SqlUpgradeEligibility", "$SnapShotId = \"{{ SQLServerSnapshotId }}\"", "Validate-InstanceLicensing($SnapShotId)", "}", "catch {", "Write-Error $_.Exception.Message", "exit 1", "}" ]
      }
    },
    "isCritical" : "true",
    "nextStep" : "describeOriginalInstanceDetails"
  }, {
    "name" : "describeOriginalInstanceDetails",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "Abort",
    "timeoutSeconds" : 300,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "InstanceIds" : [ "{{ InstanceId }}" ]
    },
    "outputs" : [ {
      "Name" : "InstanceType",
      "Selector" : "$.Reservations[0].Instances[0].InstanceType",
      "Type" : "String"
    }, {
      "Name" : "KeyName",
      "Selector" : "$.Reservations[0].Instances[0].KeyName",
      "Type" : "String"
    }, {
      "Name" : "AvailabilityZone",
      "Selector" : "$.Reservations[0].Instances[0].Placement.AvailabilityZone",
      "Type" : "String"
    }, {
      "Name" : "RootDeviceName",
      "Selector" : "$.Reservations[0].Instances[0].RootDeviceName",
      "Type" : "String"
    }, {
      "Name" : "SubnetId",
      "Selector" : "$.Reservations[0].Instances[0].NetworkInterfaces[0].SubnetId",
      "Type" : "String"
    }, {
      "Name" : "Platform",
      "Selector" : "$.Reservations[0].Instances[0].Platform",
      "Type" : "String"
    }, {
      "Name" : "SecurityGroupIds",
      "Selector" : "$.Reservations[0].Instances[0].SecurityGroups..GroupId",
      "Type" : "StringList"
    } ],
    "isCritical" : "false",
    "nextStep" : "assertRootVolumeIsEbs"
  }, {
    "name" : "assertRootVolumeIsEbs",
    "action" : "aws:assertAwsResourceProperty",
    "onFailure" : "Abort",
    "maxAttempts" : 2,
    "timeoutSeconds" : 300,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeInstances",
      "InstanceIds" : [ "{{ InstanceId }}" ],
      "PropertySelector" : "$.Reservations[0].Instances[0].RootDeviceType",
      "DesiredValues" : [ "ebs" ]
    },
    "isCritical" : "true",
    "nextStep" : "branchIfWindowsUpgradeRequired"
  }, {
    "name" : "branchIfWindowsUpgradeRequired",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "putWindowsUpgradeRequiredMetric",
        "Variable" : "{{ assertReadyToUpgradeSQLServer.Output }}",
        "Contains" : "WINDOWSUPGRADE 2012"
      }, {
        "NextStep" : "putWindows2016UpgradeRequiredMetric",
        "Variable" : "{{ assertReadyToUpgradeSQLServer.Output }}",
        "Contains" : "WINDOWSUPGRADE 2016"
      } ],
      "Default" : "branchOnRebootInstanceBeforeTakingImage"
    },
    "isCritical" : "true"
  }, {
    "name" : "putWindowsUpgradeRequiredMetric",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeSQLServer",
      "MetricName" : "Windows_Upgrade_Required",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "nextStep" : "upgradeWindowsFirst"
  }, {
    "name" : "putWindows2016UpgradeRequiredMetric",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeSQLServer",
      "MetricName" : "Windows_Upgrade_Required",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "nextStep" : "upgradeWindows2016First"
  }, {
    "name" : "putNoWindowsUpgradeRequiredMetric",
    "action" : "aws:putServerUpgradeMetrics",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DimensionName" : "SSM_METRICS",
      "DimensionValue" : "AWSEC2-CloneInstanceAndUpgradeSQLServer",
      "MetricName" : "No_Windows_Upgrade_Required",
      "MetricValue" : "1.0"
    },
    "isCritical" : "true",
    "nextStep" : "branchOnRebootInstanceBeforeTakingImage"
  }, {
    "name" : "upgradeWindowsFirst",
    "action" : "aws:executeAutomation",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DocumentName" : "AWSEC2-CloneInstanceAndUpgradeWindows",
      "RuntimeParameters" : {
        "InstanceId" : [ "{{ InstanceId }}" ],
        "IamInstanceProfile" : [ "{{ IamInstanceProfile }}" ],
        "SubnetId" : [ "{{ SubnetId }}" ],
        "AlternativeKeyPairName" : [ "{{ AlternativeKeyPairName }}" ]
      }
    },
    "isCritical" : "true",
    "nextStep" : "getWindowsUpgradeAMI"
  }, {
    "name" : "upgradeWindows2016First",
    "action" : "aws:executeAutomation",
    "maxAttempts" : 3,
    "onFailure" : "Abort",
    "inputs" : {
      "DocumentName" : "AWSEC2-CloneInstanceAndUpgradeWindows",
      "RuntimeParameters" : {
        "InstanceId" : [ "{{ InstanceId }}" ],
        "IamInstanceProfile" : [ "{{ IamInstanceProfile }}" ],
        "SubnetId" : [ "{{ SubnetId }}" ],
        "AlternativeKeyPairName" : [ "{{ AlternativeKeyPairName }}" ],
        "TargetWindowVersion" : [ "2016" ]
      }
    },
    "isCritical" : "true",
    "nextStep" : "getWindows2016UpgradeAMI"
  }, {
    "name" : "getWindowsUpgradeAMI",
    "action" : "aws:executeAwsApi",
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ssm",
      "Api" : "GetAutomationExecution",
      "AutomationExecutionId" : "{{ upgradeWindowsFirst.ExecutionId }}"
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.AutomationExecution.Outputs.'UpgradedTo2012R2.ImageId'[0]",
      "Type" : "String"
    } ],
    "isCritical" : "true",
    "nextStep" : "addUniqueTagForTheAMI"
  }, {
    "name" : "getWindows2016UpgradeAMI",
    "action" : "aws:executeAwsApi",
    "onFailure" : "Abort",
    "inputs" : {
      "Service" : "ssm",
      "Api" : "GetAutomationExecution",
      "AutomationExecutionId" : "{{ upgradeWindows2016First.ExecutionId }}"
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.AutomationExecution.Outputs.'UpgradedFrom2012R2Or2016.ImageId'[0]",
      "Type" : "String"
    } ],
    "isCritical" : "true",
    "nextStep" : "addUniqueTagForThe2016AMI"
  }, {
    "name" : "addUniqueTagForTheAMI",
    "action" : "aws:createTags",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "ResourceIds" : [ "{{ getWindowsUpgradeAMI.ImageId }}" ],
      "Tags" : [ {
        "Key" : "AWSEC2-ORIG-IMAGE-{{ automation:EXECUTION_ID }}",
        "Value" : "AWSEC2-IMAGE: {{ InstanceId }}"
      } ],
      "ResourceType" : "EC2"
    },
    "isCritical" : "true",
    "nextStep" : "describeOriginalInstanceImage"
  }, {
    "name" : "addUniqueTagForThe2016AMI",
    "action" : "aws:createTags",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "ResourceIds" : [ "{{ getWindows2016UpgradeAMI.ImageId }}" ],
      "Tags" : [ {
        "Key" : "AWSEC2-ORIG-IMAGE-{{ automation:EXECUTION_ID }}",
        "Value" : "AWSEC2-IMAGE: {{ InstanceId }}"
      } ],
      "ResourceType" : "EC2"
    },
    "isCritical" : "true",
    "nextStep" : "describeOriginalInstanceImage"
  }, {
    "name" : "branchOnRebootInstanceBeforeTakingImage",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "createImageWithReboot",
        "Variable" : "{{ RebootInstanceBeforeTakingImage }}",
        "EqualsIgnoreCase" : "True"
      } ],
      "Default" : "createImageWithNoReboot"
    },
    "isCritical" : "true"
  }, {
    "name" : "createImageWithReboot",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "CreateImage",
      "InstanceId" : "{{ InstanceId }}",
      "Name" : "AWSEC2SQLSERVER_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}",
      "NoReboot" : false
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "getImageWhereNoWindowUpgradeHappen"
  }, {
    "name" : "createImageWithNoReboot",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "CreateImage",
      "InstanceId" : "{{ InstanceId }}",
      "Name" : "AWSEC2SQLSERVER_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}",
      "NoReboot" : true
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "getImageWhereNoWindowUpgradeHappen"
  }, {
    "name" : "getImageWhereNoWindowUpgradeHappen",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeImages",
      "Filters" : [ {
        "Name" : "name",
        "Values" : [ "AWSEC2SQLSERVER_ImageFromOriginalInstance_{{ automation:EXECUTION_ID }}" ]
      } ]
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.Images[0].ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "addTagForTheAMINoWindowUpgrade"
  }, {
    "name" : "addTagForTheAMINoWindowUpgrade",
    "action" : "aws:createTags",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "ResourceIds" : [ "{{ getImageWhereNoWindowUpgradeHappen.ImageId }}" ],
      "Tags" : [ {
        "Key" : "AWSEC2-ORIG-IMAGE-{{ automation:EXECUTION_ID }}",
        "Value" : "AWSEC2-IMAGE: {{ InstanceId }}"
      } ],
      "ResourceType" : "EC2"
    },
    "isCritical" : "true",
    "nextStep" : "describeOriginalInstanceImage"
  }, {
    "name" : "describeOriginalInstanceImage",
    "action" : "aws:executeAwsApi",
    "maxAttempts" : 2,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeImages",
      "Filters" : [ {
        "Name" : "tag-key",
        "Values" : [ "AWSEC2-ORIG-IMAGE-{{ automation:EXECUTION_ID }}" ]
      } ]
    },
    "outputs" : [ {
      "Name" : "ImageId",
      "Selector" : "$.Images[0].ImageId",
      "Type" : "String"
    } ],
    "nextStep" : "waitTillOriginalInstanceImageImageReady"
  }, {
    "name" : "waitTillOriginalInstanceImageImageReady",
    "action" : "aws:waitForAwsResourceProperty",
    "onFailure" : "step:deleteServerUpgradeInstance",
    "maxAttempts" : 2,
    "inputs" : {
      "Service" : "ec2",
      "Api" : "DescribeImages",
      "ImageIds" : [ "{{ describeOriginalInstanceImage.ImageId }}" ],
      "PropertySelector" : "$.Images[0].State",
      "DesiredValues" : [ "available" ]
    },
    "nextStep" : "branchUseAlternativeKeyPair"
  }, {
    "name" : "branchUseAlternativeKeyPair",
    "action" : "aws:branch",
    "onFailure" : "Abort",
    "inputs" : {
      "Choices" : [ {
        "NextStep" : "serverUpgradeInstanceWithAlternativeKeyPair",
        "Not" : {
          "Variable" : "{{ AlternativeKeyPairName }}",
          "StringEquals" : ""
        }
      } ],
      "Default" : "serverUpgradeInstanceWithOriginalKeyPair"
    },
    "isCritical" : "true"
  }, {
    "name" : "serverUpgradeInstanceWithAlternativeKeyPair",
    "action" : "aws:runInstances",
    "timeoutSeconds" : 2700,
    "maxAttempts" : 3,
    "onFailure" : "step:deleteServerUpgradeInstance",
    "inputs" : {
      "ImageId" : "{{ describeOriginalInstanceImage.ImageId }}",
      "InstanceType" : "{{ describeOriginalInstanceDetails.InstanceType }}",
      "MinInstanceCount" : 1,
      "MaxInstanceCount" : 1,
      "IamInstanceProfileName" : "{{ IamInstanceProfile }}",
      "SubnetId" : "{{ SubnetId }}",
      "KeyName" : "{{ AlternativeKeyPairName }}",
      "SecurityGroupIds" : [ "{{ describeOriginalInstanceDetails.SecurityGroupIds }}" ],
      "TagSpecifications" : [ {
        "ResourceType" : "instance",
        "Tags" : [ {
          "Key" : "Name",
          "Value" : "AWSEC2_UpgradeInstance_{{ automation:EXECUTION_ID }}"
        } ]
      } ]
    },
    "isCritical" : "true",
    "nextStep" : "getServerUpgradeInstance"
  }, {
