{
  "schemaVersion" : "2.2",
  "description" : "Get latest syllable code",
  "parameters" : {
    "accesskeyPath" : {
      "type" : "String",
      "description" : "param store location"
    },
    "workDir" : {
      "type" : "String",
      "description" : "work dir",
      "default" : "/tmp"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:downloadContent",
    "name" : "checkoutterraform",
    "inputs" : {
      "sourceType" : "GitHub",
      "sourceInfo" : {
        "owner" : "asksyllable",
        "repository" : "terraform",
        "getOptions" : "branch:prod_env_setup",
        "path" : ".",
        "tokenInfo" : "{{ ssm-secure:{{accesskeyPath}} }}"
      },
      "destinationPath" : "{{ workDir }}/terraform"
    }
  }, {
    "action" : "aws:runShellScript",
    "name" : "permupdates",
    "inputs" : {
      "runCommand" : [ "chown -R ssm-user:ssm-user {{ workDir}}/terraform", "ls {{ workDir}}/terraform" ],
      "workingDirectory" : "{{ workDir }}/terraform"
    }
  } ]
}
