{
  "description" : "### Document name - AWSConfigRemediation-ELBInternetFacing

## What does this document do?
The runbook enables deletion of Internet facing ELB 

## Input Parameters
* LoadBalancerId: (Required) The Classic Load Balancer name or the Application Load Balancer ARN.
* AutomationAssumeRole: (Optional) The Amazon Resource Name (ARN) of the AWS Identity and Access Management (IAM) role that allows Systems Manager Automation to perform the actions on your behalf.
* TopicArn: (Required) The ARN of the SNS topic to publish the notification to.

## Output Parameters
* ELBInternetFacing.Output: The standard HTTP response from the delete_load_balancer API call.
",
  "schemaVersion" : "0.3",
  "assumeRole" : "{{ AutomationAssumeRole }}",
  "outputs" : [ "ELBInternetFacing.Output" ],
  "parameters" : {
    "LoadBalancerId" : {
      "type" : "String",
      "description" : "(Required) The Amazon Resource Name (ARN) LoadBalancer Id",
      "allowedPattern" : "^arn:(aws[a-zA-Z-]*)?:elasticloadbalancing:.*:\d{12}:loadbalancer\/[\w+=,.@\/-]+$|^[a-zA-Z0-9]([a-zA-Z0-9\-]{0,30}[a-zA-Z0-9])?$"
    },
    "AutomationAssumeRole" : {
      "type" : "String",
      "description" : "(Optional) The ARN of the role that allows Automation to perform the actions on your behalf.",
      "allowedPattern" : "^arn:(aws[a-zA-Z-]*)?:iam::\d{12}:role/[\w+=,.@/-]+$",
      "default" : ""
    },
    "TopicArn" : {
      "type" : "String",
      "description" : "(Required) The ARN of the SNS topic to publish the notification to."
    }
  },
  "mainSteps" : [ {
    "name" : "ELBInternetFacing",
    "action" : "aws:executeScript",
    "description" : "## ELBInternetFacing
Enables and verifies the logging for the Classic Load Balancer or the Application Load Balancer. 
## Outputs
* Output: The standard HTTP response from the ModifyLoadBalancerAttributes API call.
",
    "isEnd" : true,
    "timeoutSeconds" : 600,
    "inputs" : {
      "Runtime" : "python3.7",
      "Handler" : "handler",
      "InputPayload" : {
        "ResourceId" : "{{ LoadBalancerId }}"
      },
      "Script" : "import boto3
import json
import re
import botocore

def is_elb_logging_enabled(resource_type, elb_client, resource_id):
  if resource_type == 'AWS::ElasticLoadBalancing::LoadBalancer':
    return is_classic_elb_logging_enabled(elb_client, resource_id)
  elif resource_type == 'AWS::ElasticLoadBalancingV2::LoadBalancer':
    return is_application_elb_logging_enabled(elb_client, resource_id)

def is_classic_elb_logging_enabled(elb_client, resource_id):
  load_balancer_attributes = elb_client.describe_load_balancer_attributes(LoadBalancerName=resource_id)
  if (load_balancer_attributes == null):
    return True
  return False

def is_application_elb_logging_enabled(elb_client, resource_id):
  load_balancer_attributes = elb_client.describe_load_balancer_attributes(LoadBalancerArn=resource_id)
  if (load_balancer_attributes == null):
    return True
  return False

def update_application_elb_deletion(elb_client, resource_id):
  response = elb_client.delete_load_balancer(
      LoadBalancerArn=resource_id
  )
  return response

def update_application_elb_deletion(elb_client, resource_id):
  response = elb_client.delete_load_balancer(
      LoadBalancerArn=resource_id
  )
  return response

def handler(event, context):
  resource_id = event["ResourceId"]
  elb_client = boto3.client("elb")
  ALB_REGEX = "^arn:(aws[a-zA-Z-]*)?:elasticloadbalancing:.*:\d{12}:loadbalancer\/[\w+=,.@\/-]+$"

  if re.match(ALB_REGEX, resource_id):
    resource_type = "AWS::ElasticLoadBalancingV2::LoadBalancer"
  else:
    resource_type = "AWS::ElasticLoadBalancing::LoadBalancer"

  update_response = {}
  if resource_type == 'AWS::ElasticLoadBalancingV2::LoadBalancer':
    elb_client = boto3.client("elbv2")
    update_response = update_application_elb_deletion(elb_client, resource_id)
  elif resource_type == 'AWS::ElasticLoadBalancing::LoadBalancer':
    update_response = update_classic_elb_deletion(elb_client, resource_id)
  else:
    raise Exception(
      "THE RESOURCE TYPE {} IS NOT SUPPORTED FOR THIS REMEDIATION DOCUMENT AWSConfigRemediation-ELBInternetFacing.".format(resource_type)
    )

  try:
    if is_elb_logging_enabled(resource_type, elb_client, resource_id):
        return {
            "output": {
                "Message": "Verification of enable logging on Elastic Load Balancer {} is successful.".format(resource_id),
                "HTTPResponse": update_response["ResponseMetadata"]
            }
        }
    raise Exception(
        "VERIFICATION FAILED. ENABLE LOGGING ON ELASTIC LOAD BALANCER {} IS UNSUCCESSFUL.".format(resource_id)
    )
  except Exception as e:
        return {
            "output": {
                "Message": "Successfuly deleted non-compliant Elastic Load Balancer {} .".format(resource_id),
                "HTTPResponse": update_response["ResponseMetadata"]
            }
        }"
    },
    "outputs" : [ {
      "Name" : "Output",
      "Selector" : "$.Payload.output",
      "Type" : "StringMap"
    } ]
  }, {
    "name" : "PublishSNSNotification",
    "action" : "aws:executeAwsApi",
    "inputs" : {
      "Service" : "sns",
      "Api" : "Publish",
      "TopicArn" : "{{TopicArn}}",
      "Message" : "Non Compliant resource remediated through automation {{ LoadBalancerId }}"
    }
  } ]
}
