        "RuleCondition" : {
          "Fn::Or" : [ {
            "Fn::Equals" : [ {
              "Ref" : "PerRegionSharedResources"
            }, "AutoDetect" ]
          }, {
            "Fn::Equals" : [ {
              "Ref" : "PerAccountSharedResources"
            }, "AutoDetect" ]
          } ]
        },
        "Assertions" : [ {
          "Assert" : {
            "Fn::And" : [ {
              "Fn::Equals" : [ {
                "Ref" : "PerRegionSharedResources"
              }, "AutoDetect" ]
            }, {
              "Fn::Equals" : [ {
                "Ref" : "PerAccountSharedResources"
              }, "AutoDetect" ]
            } ]
          },
          "AssertDescription" : "AutDetect must be set/unset for both PerRegionSharedResources and PerAccountSharedResources"
        } ]
      },
