Content:
{
  "schemaVersion": "0.3",
  "description": "Install UpdatesAndTagInstance",
  "assumeRole": "{{ AutomationAssumeRole }}",
  "parameters": {
    "InstanceId": {
      "type": "String",
      "description": "Instance ID to be patched"
      },
      "AutomationAssumeRole": {
        "type": "String",
        "description": "(Required) The ARN of the role that allows Automation to perform the actions on your behalf.",
        "default": "arn:aws:iam::{{global:ACCOUNT_ID}}:role/SSMAutomationRole"
        }
  },
  "mainSteps": [
  {
    "name": "RunPatchBaseline",
    "action": "aws:runCommand",
    "maxAttempts": 3,
    "onFailure": "Abort",
    "timeoutSeconds": 7200,
    "inputs": {
      "DocumentName": "AWS-RunPatchBaseline",
      "InstanceIds": [
        "{{InstanceId}}"
        ],
      "Parameters": {
        "Operation": "Install"
        },
      "OutputS3BucketName": "w-torq-usw2-dev-ssm-patching",
      "OutputS3KeyPrefix": "logs"
      }
    },
  {
    "name": "RemoveKernel",
    "action": "aws:runCommand",
    "maxAttempts": 3,
    "onFailure": "Continue",
    "timeoutSeconds": 7200,
    "inputs": {
      "DocumentName": "wabtec-kernel-cleanup-doc",
      "InstanceIds": [
        "{{InstanceId}}"
        ]
      }
    },
  {
    "name": "createTags",
    "action": "aws:createTags",
    "maxAttempts": 3,
    "onFailure": "Abort",
    "inputs": {
      "ResourceType": "EC2",
      "ResourceIds": [
        "{{InstanceId}}"
        ],
      "Tags": [
      {
    "Key": "patch-run-date",
    "Value": "{{global:DATE}}"
      }
      ]
    }
  }
]
}
