{
  "schemaVersion" : "2.2",
  "description" : "Palantir",
  "parameters" : {
    "customer" : {
      "description" : "The customer shortname",
      "type" : "String"
    },
    "environment" : {
      "description" : "The palantir environment [sandbox | prod]",
      "type" : "String"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "PalantirLinux",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "runCommand" : [ "sudo su - root 2>&1 > /dev/null", "mkdir -p /root/palantir/json 2>&1 > /dev/null", "aws s3 cp --only-show-errors s3://palantir-10006-inventory/palantir-artifacts/{{ environment }}/ec2_python.zip /root/palantir 2>&1 > /dev/null", "unzip /root/palantir/ec2_python.zip -d /root/palantir 2>&1 > /dev/null", "python /root/palantir/palantir.py {{ customer }}", "aws s3 sync /root/palantir/json/ s3://ellucian-{{ customer }}-artifacts/palantir", "rm -rf /root/palantir 2>&1 > /dev/null" ]
    }
  }, {
    "action" : "aws:runPowerShellScript",
    "name" : "PalantirWindows",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "runCommand" : [ "mkdir c:/Ellucian/palantir/json", "aws s3 cp --only-show-errors s3://palantir-10006-inventory/palantir-artifacts/{{ environment }}/ec2_powershell.zip c:/Ellucian/palantir/ec2_powershell.zip", "Expand-Archive -Path c:\\Ellucian\\palantir\\ec2_powershell.zip -DestinationPath c:\\Ellucian\\palantir", ". C:\\\\Ellucian\\\\palantir\\\\ec2_audit.ps1 {{ customer }}", "aws s3 sync c:/Ellucian/palantir/json/ s3://ellucian-{{ customer }}-artifacts/palantir", "Remove-Item 'C:\\Ellucian\\palantir' -Recurse -Confirm:$false -Force" ]
    }
  } ]
}
