Content:
{
  "description": "aws:downloadContent",
  "schemaVersion": "2.2",
  "parameters": {
    "sourceInfo": {
      "default": {
        "allowInsecureDownload": true,
        "url": "http://<url>"
      },
      "description": "(Required) The information required to retrieve the content from the required source.",
      "type": "StringMap"
    },
    "sourceType": {
      "allowedValues": [
        "HTTP",
        "S3",
        "Git",
        "GitHub",
        "SSMDocument"
      ],
      "default": "HTTP",
      "description": "(Required) The download source.",
      "type": "String"
    },
    "destinationPath": {
      "description": "(Optional) Local path on the instance where you want to download the file.",
      "type": "String"
    }
  },
  "mainSteps": [
    {
      "inputs": {
        "sourceInfo": "{{ sourceInfo }}",
        "sourceType": "{{ sourceType }}",
        "destinationPath": "{{ destinationPath }}"
      },
      "name": "downloadContent",
      "action": "aws:downloadContent"
    }
  ]
}
