def get_queue_url_from_arn(sqs_client, queue_arn: str) -> dict:
    queue_name = queue_arn.split(':')[-1]
    queue_account_id = queue_arn.split(':')[-2]

    if (not queue_name or not queue_account_id):
        raise ValueError(
            f'{"Subscription target sqs queue is invalid"}')

    get_queue_url_response = sqs_client.get_queue_url(
        QueueName=queue_name,
        QueueOwnerAWSAccountId=queue_account_id
    )

    return get_queue_url_response['QueueUrl']



def get_queue_policy(sqs_client, queue_url: str) -> dict:
    get_queue_attributes_response = sqs_client.get_queue_attributes(
        QueueUrl=queue_url,
        AttributeNames=['All'],
    )

    sqs_endpoint_policy = get_queue_attributes_response['Attributes']['Policy']

    return sqs_endpoint_policy



def backup_sns_sub_with_sqs_target(sub_arn: str, should_backup_dlq: bool) -> dict:
    config = Config(
        signature_version='v4',
        retries={
            'max_attempts': 10,
            'mode': 'standard'
        }
    )
    sns_client = boto3.client('sns', config=config)
    sqs_client = boto3.client("sqs", config=config)

    sub_attributes = sns_client.get_subscription_attributes(SubscriptionArn=sub_arn)['Attributes']

    if (not sub_attributes.get('RedrivePolicy')):
        raise ValueError(
            f'{"Redrive policy (dlq) must be present on the subscription"}')

    sub_sqs_endpoint_arn = sub_attributes['Endpoint']

    sub_endpoint_queue_url = get_queue_url_from_arn(sqs_client, sub_sqs_endpoint_arn)
    sub_endpoint_queue_policy = get_queue_policy(sqs_client, sub_endpoint_queue_url)

    if not should_backup_dlq:
        return {'TopicArn': sub_attributes['TopicArn'],
                'SqsEndpointQueueUrl': sub_endpoint_queue_url,
                'SqsEndpointQueueArn': sub_sqs_endpoint_arn,
                'SqsEndpointPolicy': sub_endpoint_queue_policy}

    redrive_policy_parsed = json.loads(sub_attributes['RedrivePolicy'])
    dlq_arn = redrive_policy_parsed['deadLetterTargetArn']

    dlq_url = get_queue_url_from_arn(sqs_client, dlq_arn)
    sub_dlq_policy = get_queue_policy(sqs_client, dlq_url)

    return {'TopicArn': sub_attributes['TopicArn'],
            'SqsEndpointQueueUrl': sub_endpoint_queue_url,
            'SqsEndpointQueueArn': sub_sqs_endpoint_arn,
            'SqsDlqUrl': dlq_url,
            'SqsDlqArn': dlq_arn,
            'SqsEndpointPolicy': sub_endpoint_queue_policy,
            'SqsDlqPolicy': sub_dlq_policy}



def broken_redrive_policy_target_backup_current_execution(events: dict, context: dict) -> dict:
    sub_arn: str = events['SubscriptionArn']
    backup = backup_sns_sub_with_sqs_target(sub_arn, should_backup_dlq=True)

    return backup"
