{
  "description" : "sudo yum update",
  "schemaVersion" : "2.2",
  "parameters" : {
    "package" : {
      "type" : "String",
      "description" : "package to be updated",
      "default" : "kernel"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "ConfigureKernelLivePatching",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "timeoutSeconds" : 7200,
      "runCommand" : [ "#!/bin/bash", "#", "yes | sudo yum update {{package}}" ]
    }
  } ]
}
