{
  "schemaVersion": "1.2",
  "description": "Configure the CloudWatch agent to collect Disk Usage and Memory Usage metrics",
  "parameters": {},
  "runtimeConfig": {
    "aws:cloudWatch": {
      "metrics_collected": {
        "disk": {
          "measurement": [
            "used_percent"
          ],
          "resources": [
            "/"
          ]
        },
        "mem": {
          "measurement": [
            "mem_used_percent"
          ]
        }
      }
    }
  }
}
