{
  "schemaVersion": "2.2",
  "description": "Clean up old kernel",
  "parameters": {
    "Message": {
      "type": "String",
      "description": "Linux kernel clean up",
      "default": "Cleaning up old kernels."
    }
  },
  "mainSteps": [
    {
      "precondition": {
        "StringEquals": [
          "platformType",
          "Linux"
        ]
      },
      "action": "aws:runShellScript",
      "name": "RemoveKernel",
      "inputs": {
        "runCommand": [
          "sudo yum install yum-utils",
          "yes | sudo package-cleanup --oldkernels --count=1"
        ]
      }
    }
  ]
}
