{
  "description" : "sudo yum update",
  "schemaVersion" : "2.2",
  "parameters" : {
    "package" : {
      "type" : "String",
      "description" : "package to be updated",
      "default" : "kernel"
    },
    "restartKernel" : {
      "type" : "Boolean",
      "description" : "should we restart the kernels?",
      "default" : false
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runShellScript",
    "name" : "ConfigureKernelLivePatching",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "timeoutSeconds" : 7200,
      "runCommand" : [ "#!/bin/bash", "#", "yes | sudo yum update {{package}}", "if [ restart_kernel ] ", "then", "  sudo yum update kernel", "  sudo yum install yum-util", "  sudo package-cleanup --oldkernels --count=1", "fi" ]
    }
  } ]
}
