{
  "description" : "This action will install Splunk Agents on an EC2 instance.\n",
  "schemaVersion" : "2.2",
  "parameters" : {
    "playbookurl" : {
      "type" : "String",
      "description" : "URL where the playbook is stored",
      "allowedPattern" : "^\\s*$|^(s3)://[^']*$"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runPowerShellScript",
    "name" : "InstallWindowsAgent",
    "precondition" : {
      "StringEquals" : [ "platformType", "Windows" ]
    },
    "inputs" : {
      "timeoutSeconds" : "3600",
      "runCommand" : [ "try {", "}", "catch {", "Write-Error \"General error during agent installation\"", "Write-Error $_", "Write-Error $_.ScriptStackTrace", "cd ..", "Remove-Item -Force -Recurse $tmpDir", "exit 1", "}" ]
    }
  }, {
    "action" : "aws:runShellScript",
    "name" : "runShellScript",
    "precondition" : {
      "StringEquals" : [ "platformType", "Linux" ]
    },
    "inputs" : {
      "timeoutSeconds" : "3600",
      "runCommand" : [ "#!/bin/bash", "ansible --version", "if [ $? -ne 0 ]; then", " echo \"Ansible is not installed. Please install Ansible and rerun the command\" >&2", " exit 1", "fi", "execdir=$(dirname $0)", "cd $execdir", "aws s3 cp '{{ playbookurl }}' playbook.yml", "if [ $? -ne 0 ]; then", "  echo \"Error while downloading the document from S3\" >&2", "  exit 1", "fi", "ansible-playbook -i \"localhost,\" -c local playbook.yml" ]
    }
  } ]
}
