{
  "schemaVersion" : "2.2",
  "description" : "Install IIS on an EC2 Windows instance using Ansible playbooks",
  "parameters" : {
    "Message" : {
      "type" : "String",
      "description" : "The ID of the EC2 instance to install IIS on",
      "default" : "Hello World"
    }
  },
  "mainSteps" : [ {
    "action" : "aws:runPowerShellScript",
    "name" : "example",
    "inputs" : {
      "runCommand" : [ "Write-Host \"Installing IIS...\"", "sudo apt-get install ansible", "ansible-playbook -i inventory playbook.yaml" ]
    }
  } ]
}
